import { describe, it, expect, beforeEach, afterEach, vi } from 'vitest';
import * as fs from 'fs/promises';
import * as path from 'path';
import * as os from 'os';

let userDataDir: string;

vi.mock('electron', () => {
  const api = {
    app: {
      getPath: (name: string) => {
        if (name === 'userData') {
          return userDataDir;
        }
        return '';
      }
    },
    safeStorage: {
      isEncryptionAvailable: () => true,
      encryptString: (s: string) => Buffer.from('enc:' + s, 'utf8'),
      decryptString: (b: Buffer) => Buffer.from(b).toString('utf8').replace(/^enc:/, '')
    }
  };
  return api;
});

const importService = async () => {
  vi.resetModules();
  const mod = await import('../../main/services/SettingsService');
  return mod.default as any;
};

const settingsFilePath = () => path.join(userDataDir, 'settings.json');

const makeTempUserData = async () => {
  const prefix = path.join(os.tmpdir(), 'ra-userdata-');
  return await fs.mkdtemp(prefix);
};

beforeEach(async () => {
  userDataDir = await makeTempUserData();
});

afterEach(async () => {
  try {
    await fs.rm(userDataDir, { recursive: true, force: true });
  } catch {}
});

describe('SettingsService security and persistence', () => {
  it('saves settings with encrypted apiKey and loads back decrypted', async () => {
    const service = await importService();
    const PLAINTEXT = 'test-openai-key';

    const saveRes = await service.saveSettings({
      providers: {
        openai: { enabled: true, apiKey: PLAINTEXT, model: 'gpt-4-turbo-preview' }
      }
    });
    expect(saveRes.success).toBe(true);

    const raw = await fs.readFile(settingsFilePath(), 'utf8');
    const parsed = JSON.parse(raw);
    const stored = parsed?.providers?.openai?.apiKey;

    expect(typeof stored).toBe('string');
    // base64-like check and not equal to plaintext
    expect(/^[A-Za-z0-9+/=]+$/.test(stored)).toBe(true);
    expect(stored === PLAINTEXT).toBe(false);

    const loaded = await service.loadSettings();
    // Avoid printing secrets on assertion failure
    expect(loaded?.providers?.openai?.apiKey === PLAINTEXT).toBe(true);
  });

  it('returns defaults when settings.json is missing', async () => {
    const service = await importService();
    // ensure file is absent
    try { await fs.rm(settingsFilePath(), { force: true }); } catch {}

    const loaded = await service.loadSettings();

    expect(loaded?.general?.theme).toBe('light');
    expect(loaded?.providers?.openai?.apiKey === '').toBe(true);
  });

  it('returns defaults on corrupted settings.json', async () => {
    const service = await importService();

    await fs.mkdir(userDataDir, { recursive: true });
    await fs.writeFile(settingsFilePath(), 'not json', 'utf8');

    const loaded = await service.loadSettings();

    expect(loaded?.general?.theme).toBe('light');
    expect(loaded?.providers?.openai?.apiKey === '').toBe(true);
  });
});

import { describe, it, expect, vi, beforeAll, beforeEach } from 'vitest';
import { IPC_CHANNELS } from '../../shared/constants';

// Hoisted state so it's initialized before hoisted mocks execute
const hoisted = vi.hoisted(() => {
  return {
    registry: {} as Record<string, Function>,
    mockSend: vi.fn(),
    windows: [] as any[],
  };
});

// Mock electron to provide minimal app/ipcMain/BrowserWindow used by main code
vi.mock('electron', () => {
  const { registry, mockSend, windows } = hoisted;

  class MockBrowserWindow {
    static getAllWindows = vi.fn(() => windows);
    public webContents: any;
    constructor(..._args: any[]) {
      this.webContents = {
        send: mockSend,
        on: vi.fn(),
        openDevTools: vi.fn(),
      };
      (this as any).once = vi.fn();
      (this as any).on = vi.fn();
      (this as any).show = vi.fn();
      (this as any).hide = vi.fn();
      (this as any).setMenuBarVisibility = vi.fn();
      (this as any).loadURL = vi.fn();
      (this as any).loadFile = vi.fn();
      (this as any).isDestroyed = vi.fn(() => false);
      windows.push(this);
    }
  }

  return {
    app: {
      whenReady: () => Promise.resolve(),
      on: vi.fn(),
      off: vi.fn(),
      quit: vi.fn(),
    },
    ipcMain: {
      handle: vi.fn((channel: string, fn: Function) => {
        registry[channel] = fn;
      }),
      on: vi.fn(),
      removeHandler: vi.fn(),
      removeAllListeners: vi.fn(),
    },
    BrowserWindow: MockBrowserWindow as any,
    dialog: {
      showOpenDialog: vi.fn(async () => ({ canceled: true, filePaths: [] })),
      showSaveDialog: vi.fn(async () => ({ canceled: true, filePath: undefined })),
    },
  };
});

// Ensure electron-squirrel-startup import is inert
vi.mock('electron-squirrel-startup', () => ({}));

// Break circular import: mock the app entry to avoid executing real index.ts during handlers import
vi.mock('../../main/index', () => {
  // Provide a stub mainWindow export referenced by handlers
  return { mainWindow: undefined };
});

// Mock SceneRewriter used by the GENERATE_REWRITE handler
vi.mock('../../services/rewrite/SceneRewriter', () => {
  return {
    default: vi.fn().mockImplementation(() => {
      return {
        rewriteScene: vi.fn().mockResolvedValue({
          success: true,
          rewrittenText: 'rewritten text',
          issuesAddressed: [],
          changesExplanation: 'mocked explanation',
          preservedElements: [],
          diffData: [],
          modelUsed: 'mock-model',
        }),
      };
    }),
  };
});

// Import handlers after mocks and invoke registration directly.
// Also ensure at least one BrowserWindow exists so progress events can be emitted.
beforeAll(async () => {
  const { setupIPCHandlers } = await import('../../main/handlers');
  setupIPCHandlers();

  const { BrowserWindow } = await import('electron');
  // Create one window instance in the mocked electron so handlers can find it
   
  new (BrowserWindow as any)();
});

describe('IPC rewrite handler', () => {
  const handlers = hoisted.registry;
  const mockSend = hoisted.mockSend;

  const resolveRewriteHandler = (): Function | undefined => {
    const preferred = handlers[IPC_CHANNELS.GENERATE_REWRITE];
    if (typeof preferred === 'function') return preferred;
    const all = Object.keys(handlers);
    return all.length ? handlers[all[0]] : undefined;
  };

  beforeEach(() => {
    mockSend.mockClear();
  });

  it('validates payload and returns error when missing scene or issues', async () => {
    const fn = resolveRewriteHandler();
    expect(fn, `Available channels: ${Object.keys(handlers).join(', ')}`).toBeTypeOf('function');

    const res1 = await (fn as Function)({}, { scene: null, issues: [] });
    // Expect standardized error shape from toErrorResponse()
    expect(res1).toEqual(
      expect.objectContaining({
        ok: false,
        error: expect.objectContaining({
          message: expect.stringContaining('Invalid'),
        }),
      })
    );

    const res2 = await (fn as Function)({}, { scene: { id: 's1', text: 'x' }, issues: [] });
    expect(res2.ok).toBe(false);
  });

  it('invokes SceneRewriter and emits progress on success', async () => {
    const fn = resolveRewriteHandler();
    expect(fn, `Available channels: ${Object.keys(handlers).join(', ')}`).toBeTypeOf('function');

    const payload = {
      sceneId: 's1',
      scene: { id: 's1', text: 'original' },
      issues: [{ id: 'i1', type: 'pronoun', severity: 'should-fix', description: 'x' }],
      previousScenes: [],
      readerContext: {
        knownCharacters: new Set(['Alice']),
        establishedTimeline: [],
        revealedPlotPoints: [],
        establishedSettings: [],
      },
      preserveElements: [],
    };

    const result = await (fn as Function)({}, payload);
    expect(result.success).toBe(true);
    expect(result.rewrittenText).toBe('rewritten text');

    expect(mockSend).toHaveBeenCalledWith(
      (IPC_CHANNELS as any).REEWRITE_PROGRESS ?? IPC_CHANNELS.REWRITE_PROGRESS, // allow minor constant drift
      expect.objectContaining({
        sceneId: 's1',
        status: 'complete',
      })
    );
  });
});

import React, { forwardRef, useImperativeHandle, useMemo, useRef, useState } from 'react';
import { useManuscriptStore } from '../stores/manuscriptStore';
import useIssueHighlighting from '../features/analyze/hooks/useIssueHighlighting';
import IssueHighlighter from '../features/analyze/components/IssueHighlighter';
import type { Scene, ReaderKnowledge, ContinuityIssue } from '../../shared/types';
import useRewriteStore from '../features/rewrite/stores/rewriteStore';
import RewriteEditor from '../features/rewrite/components/RewriteEditor';

export interface SceneViewerHandle {
  scrollToIssue(sceneId: string, issue: ContinuityIssue): void;
}

// Helper: focused reader context builder
const buildReaderContext = (previousScenes: Scene[]): ReaderKnowledge => {
  const context: ReaderKnowledge = {
    knownCharacters: new Set<string>(),
    establishedTimeline: [],
    revealedPlotPoints: [],
    establishedSettings: [],
  };

  previousScenes.forEach((scene) => {
    scene.characters?.forEach((char) => context.knownCharacters.add(char));
    scene.timeMarkers?.forEach((marker) => {
      context.establishedTimeline.push({ label: marker });
    });
    scene.locationMarkers?.forEach((loc) => {
      context.establishedSettings.push({ name: loc });
    });
    if (Array.isArray((scene as any).plotMarkers)) {
      (scene as any).plotMarkers.forEach((p: string) => {
        context.revealedPlotPoints.push(p);
      });
    }
  });

  return context;
};

const SceneViewer = forwardRef<SceneViewerHandle>((_props, ref) => {
  const { getSelectedScene, manuscript } = useManuscriptStore();
  const selectedScene = getSelectedScene();
  const analysis = selectedScene?.continuityAnalysis;

  // Rewrite workflow integration
  const { generateRewrite, hasRewrite, isRewriting, currentRewriteSceneId } = useRewriteStore();
  const [showRewriteEditor, setShowRewriteEditor] = useState(false);

  // Generate rewrite via store and open the editor
  const handleGenerateRewrite = async () => {
    if (!selectedScene || !analysis?.issues) return;
    await generateRewrite(selectedScene.id);
    setShowRewriteEditor(true);
  };

  // Is this specific scene currently generating?
  const isGeneratingForThisScene = isRewriting && currentRewriteSceneId === selectedScene?.id;

  const { buildHighlightsForScene, getScrollTarget } = useIssueHighlighting();

  const spans = useMemo(() => {
    if (!selectedScene) return [];
    return buildHighlightsForScene(selectedScene.id);
  }, [selectedScene, buildHighlightsForScene]);

  const containerRef = useRef<HTMLDivElement | null>(null);

  useImperativeHandle(ref, () => ({
    scrollToIssue(sceneId: string, issue: ContinuityIssue) {
      const root = containerRef.current;
      if (!root) return;

      const anchorId = getScrollTarget(sceneId, issue);
      const escapeAttr = (s: string) => s.replace(/"/g, '\\"');

      let target: HTMLElement | null =
        (root.querySelector(`[id="${escapeAttr(anchorId)}"]`) as HTMLElement | null) ??
        (root.querySelector(`[data-issue-id="${escapeAttr(anchorId)}"]`) as HTMLElement | null);

      if (!target) {
        const startIdx = Array.isArray(issue.textSpan) ? Number(issue.textSpan?.[0] ?? 0) : 0;
        const candidates = Array.from(root.querySelectorAll<HTMLElement>('[data-start][data-end]'));
        let best: { el: HTMLElement; dist: number } | null = null;
        for (const el of candidates) {
          const a = Number(el.getAttribute('data-start') ?? '0');
          const b = Number(el.getAttribute('data-end') ?? '0');
          const contains = a <= startIdx && startIdx < b;
          const dist = contains ? 0 : Math.min(Math.abs(startIdx - a), Math.abs(startIdx - b));
          if (!best || dist < best.dist || (dist === best.dist && a <= startIdx)) {
            best = { el, dist };
            if (dist === 0) break;
          }
        }
        target = best?.el ?? null;
      }

      if (target) {
        try {
          target.scrollIntoView({ behavior: 'smooth', block: 'center' });
        } catch {
          // no-op if scrollIntoView options unsupported
          target.scrollIntoView();
        }
        // transient flash to draw attention
        target.classList.add('ring-2', 'ring-indigo-500', 'transition-shadow');
        setTimeout(() => {
          target.classList.remove('ring-2', 'ring-indigo-500', 'transition-shadow');
        }, 1200);
      }
    },
  }));

  if (!selectedScene) {
    return (
      <div className="h-full flex items-center justify-center text-gray-500">
        <div className="text-center">
          <div className="mx-auto h-12 w-12 text-gray-400 mb-4">
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
          </div>
          <p>Select a scene to view its content</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Scene Header */}
      <div className="p-4 border-b border-gray-200 bg-gray-50">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-gray-900">
              Scene {selectedScene.position + 1}
            </h3>
            <div className="flex items-center gap-4 text-sm text-gray-600 mt-1">
              <span>{selectedScene.wordCount} words</span>
              {selectedScene.hasBeenMoved && (
                <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                  Moved from position {selectedScene.originalPosition + 1}
                </span>
              )}
              <span
                className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                  selectedScene.rewriteStatus === 'pending'
                    ? 'bg-gray-100 text-gray-800'
                    : selectedScene.rewriteStatus === 'generated'
                    ? 'bg-blue-100 text-blue-800'
                    : selectedScene.rewriteStatus === 'approved'
                    ? 'bg-green-100 text-green-800'
                    : 'bg-red-100 text-red-800'
                }`}
              >
                {selectedScene.rewriteStatus}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Scene Content */}
      <div ref={containerRef} className="flex-1 overflow-auto p-4">
        <div className="prose max-w-none">
          <IssueHighlighter
            content={selectedScene.currentRewrite || selectedScene.text}
            spans={spans}
            className="whitespace-pre-wrap text-gray-900 leading-relaxed"
          />
        </div>
      </div>

      {/* Scene Actions */}
      <div className="p-4 border-t border-gray-200 bg-gray-50">
        <div className="flex justify-between items-center">
          <div className="text-sm text-gray-600">
            {selectedScene.hasBeenMoved ? (
              <span>This scene has been moved and may need rewriting</span>
            ) : (
              <span>Scene is in its original position</span>
            )}
          </div>
          <div className="flex gap-2">
            {selectedScene.hasBeenMoved && ((analysis?.issues?.length ?? 0) > 0) && (
              <>
                {!hasRewrite(selectedScene.id) ? (
                  <button
                    onClick={handleGenerateRewrite}
                    disabled={isGeneratingForThisScene}
                    className={`px-3 py-1 text-sm font-medium rounded-md focus:outline-none focus:ring-2 ${
                      isGeneratingForThisScene
                        ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                        : 'text-green-700 bg-green-100 hover:bg-green-200 focus:ring-green-500'
                    }`}
                  >
                    {isGeneratingForThisScene ? 'Generating...' : 'Generate Rewrite'}
                  </button>
                ) : (
                  <>
                    <button
                      onClick={() => setShowRewriteEditor(true)}
                      className="px-3 py-1 text-sm font-medium text-blue-700 bg-blue-100 rounded-md hover:bg-blue-200 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      View Rewrite
                    </button>
                    <button
                      onClick={handleGenerateRewrite}
                      disabled={isGeneratingForThisScene}
                      className="px-3 py-1 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500"
                    >
                      Regenerate
                    </button>
                  </>
                )}
              </>
            )}
          </div>
        </div>
      </div>

      {/* Rewrite Editor Modal */}
      {showRewriteEditor && selectedScene && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-6xl h-5/6 flex flex-col">
            <RewriteEditor
              scene={selectedScene}
              onClose={() => setShowRewriteEditor(false)}
            />
          </div>
        </div>
      )}
    </div>
  );
});

export default SceneViewer;

import { describe, it, expect, vi, beforeEach } from 'vitest';
import type { Scene, ContinuityIssue, ReaderKnowledge } from '../../../../shared/types';
import PronounDetector from '../detectors/PronounDetector';
import TimelineDetector from '../detectors/TimelineDetector';
import CharacterDetector from '../detectors/CharacterDetector';
import PlotContextDetector from '../detectors/PlotContextDetector';
import EngagementDetector from '../detectors/EngagementDetector';
import AIServiceManager from '../../../../services/ai/AIServiceManager';
import { ValidationPipeline } from '../../../../services/ai/validation/ValidationPipeline';

type AnalyzeReq = Parameters<AIServiceManager['analyzeContinuity']>[0];

function buildScene(overrides?: Partial<Scene>): Scene {
  const base: Scene = {
    id: 's1',
    text: 'Alice looked at Bob. They went home together.',
    wordCount: 7,
    position: 3,
    originalPosition: 1,
    characters: ['Alice', 'Bob'],
    timeMarkers: [],
    locationMarkers: [],
    hasBeenMoved: true,
    rewriteStatus: 'pending',
  };
  return { ...base, ...(overrides ?? {}) } as Scene;
}

function buildPrev(n = 2): Scene[] {
  return Array.from({ length: n }).map((_, i) => ({
    id: `p${i + 1}`,
    text: `Prev ${i + 1} text with Alice and Bob context.`,
    wordCount: 6,
    position: i,
    originalPosition: i,
    characters: ['Alice'],
    timeMarkers: [],
    locationMarkers: [],
    hasBeenMoved: false,
    rewriteStatus: 'pending',
  })) as Scene[];
}

function buildReader(): ReaderKnowledge {
  return {
    knownCharacters: new Set(['Alice']),
    establishedTimeline: [],
    revealedPlotPoints: [],
    establishedSettings: [],
  };
}

function makeManagerStub(recorded: AnalyzeReq[]) {
  const stub: Pick<AIServiceManager, 'analyzeContinuity'> = {
    analyzeContinuity: vi.fn(async (req: AnalyzeReq) => {
      recorded.push(req);
      const issues: ContinuityIssue[] = []; // return empty to keep mapping deterministic
      return {
        issues,
        metadata: {
          modelUsed: (req as any).__modelId ?? 'mock',
          provider: 'openai',
          costEstimate: 0,
          durationMs: 1,
          confidence: 0.6,
          cached: false,
        },
      };
    }) as any,
  };
  return stub as AIServiceManager;
}

describe('Detector integration - AnalysisRequest enrichment and consensus adapter', () => {
  beforeEach(() => {
    vi.restoreAllMocks();
  });

  it('non-critical pronoun request: single analyze call with enriched fields', async () => {
    const calls: AnalyzeReq[] = [];
    const manager = makeManagerStub(calls);
    const det = new PronounDetector();

    const scene = buildScene({ position: 7 } as any); // no critical flag
    const prev = buildPrev(3);
    await det.detect(scene, prev, manager);

    // One AI call expected (enrichment path uses single-run)
    expect(calls.length).toBe(1);
    const req = calls[0] as any;

    // Required enriched fields presence
    expect(req).toBeTruthy();
    expect(req.scene).toBeTruthy();
    expect(req.readerContext).toBeTruthy();
    expect(req.previousScenes).toBeTruthy();

    // taskType/detector label
    expect(req.taskType).toBe('pronoun');
    expect(req.detector).toBe('pronoun');

    // sceneText and newPosition meta added (local-only)
    expect(typeof req.sceneText).toBe('string');
    expect(req.sceneText).toContain('Alice looked at Bob');

    expect(typeof req.newPosition).toBe('number');
    expect(req.newPosition).toBe(7);

    // previousScenes truncated/compacted upstream but is an array
    expect(Array.isArray(req.previousScenes)).toBe(true);

    // readerContext knownCharacters is a Set
    expect(req.readerContext.knownCharacters instanceof Set).toBe(true);
  });

  it('critical pronoun request: consensus path calls analyze twice with distinct modelId', async () => {
    const calls: AnalyzeReq[] = [];
    const manager = makeManagerStub(calls);
    const det = new PronounDetector();

    const scene = buildScene({ position: 4 } as any);
    (scene as any).critical = true; // signal critical
    const prev = buildPrev(2);

    // Spy on reconcile to ensure consensus path engaged
    const spyRecon = vi.spyOn(ValidationPipeline, 'reconcile');

    await det.detect(scene, prev, manager);

    // Two calls expected (consensus with default 2 candidates)
    expect(calls.length).toBe(2);

    const modelIds = new Set((calls.map((r: any) => r.__modelId).filter(Boolean)));
    expect(modelIds.size).toBeGreaterThanOrEqual(2); // distinct per candidate

    // Each call still carries enriched fields
    for (const req of calls as any[]) {
      expect(req.taskType).toBe('pronoun');
      expect(typeof req.sceneText).toBe('string');
    }

    expect(spyRecon).toHaveBeenCalledTimes(1);
  });

  it('timeline detector honors enrichment and consensus flags', async () => {
    const calls: AnalyzeReq[] = [];
    const manager = makeManagerStub(calls);
    const det = new TimelineDetector();

    // Provide clear temporal markers so localDetection will produce targets (and trigger AI)
    const scene = buildScene({
      text: 'Next morning, the sky cleared. Later that day, rain returned. Meanwhile, plans were made.',
    } as any);
    (scene as any).critical = true;
    const prev = buildPrev(1);

    await det.detect(scene, prev, manager);
    expect(calls.length).toBe(2);
    for (const req of calls as any[]) {
      expect(req.taskType).toBe('timeline');
      expect(typeof req.sceneText).toBe('string');
    }
  });

  it('character detector single-run when not critical', async () => {
    const calls: AnalyzeReq[] = [];
    const manager = makeManagerStub(calls);
    const det = new CharacterDetector();

    const scene = buildScene({ text: 'Eve met Mallory. "Sis," she said.' } as any);
    const prev = buildPrev(2);

    await det.detect(scene, prev, manager);
    // Could be 0 or 1 depending on targets; we assert at most one (no consensus)
    expect(calls.length).toBeLessThanOrEqual(1);
    if (calls.length === 1) {
      const req = calls[0] as any;
      expect(req.taskType).toBe('character');
    }
  });

  it('plot/engagement detectors set proper taskType and pass enriched meta', async () => {
    const callsPlot: AnalyzeReq[] = [];
    const callsEng: AnalyzeReq[] = [];
    const managerPlot = makeManagerStub(callsPlot);
    const managerEng = makeManagerStub(callsEng);

    const plot = new PlotContextDetector();
    const engage = new EngagementDetector();

    const scenePlot = buildScene({ text: 'The incident shocked the town. What happened would change everything.' } as any);
    const sceneEng = buildScene({ text: 'It was the best of times, it was the worst of times. Dialogue starts. "Hello."' } as any);

    await plot.detect(scenePlot, buildPrev(2), managerPlot);
    await engage.detect(sceneEng, buildPrev(1), managerEng);

    if (callsPlot.length > 0) {
      expect((callsPlot[0] as any).taskType).toBe('plot');
      expect(typeof (callsPlot[0] as any).sceneText).toBe('string');
    }
    if (callsEng.length > 0) {
      expect((callsEng[0] as any).taskType).toBe('engagement');
      expect(typeof (callsEng[0] as any).sceneText).toBe('string');
    }
  });
});

import React from 'react';
import type { ContinuityIssue } from '../../../../shared/types';

export interface IssueItemProps {
  issue: ContinuityIssue;
  sceneId: string;
  sceneTitle?: string;
  scenePosition?: number;
  onShowInScene?: (issue: ContinuityIssue, sceneId: string) => void;
  onDismiss?: (issue: ContinuityIssue, sceneId: string) => void;
  className?: string;
}

const SEVERITY_ORDER: Record<'must-fix' | 'should-fix' | 'consider', number> = {
  'must-fix': 3,
  'should-fix': 2,
  'consider': 1,
} as const;

const SEVERITY_LABEL: Record<'must-fix' | 'should-fix' | 'consider', string> = {
  'must-fix': 'MUST FIX',
  'should-fix': 'SHOULD FIX',
  'consider': 'CONSIDER',
} as const;

const SEVERITY_CLASS: Record<'must-fix' | 'should-fix' | 'consider', string> = {
  'must-fix': 'bg-red-500/20 text-red-700 dark:text-red-200 border border-red-500/30',
  'should-fix': 'bg-amber-500/20 text-amber-800 dark:text-amber-200 border border-amber-500/30',
  'consider': 'bg-blue-500/20 text-blue-700 dark:text-blue-200 border border-blue-500/30',
} as const;

