import React, { useCallback, useMemo } from 'react';
import type { ContinuityIssue } from '../../../../shared/types';
import { useManuscriptStore } from '../../../stores/manuscriptStore';
import useAnalysis from '../hooks/useAnalysis';
import AnalysisProgress from './AnalysisProgress';
import IssueItem from './IssueItem';

export interface IssuePanelProps {
  isOpen?: boolean;
  onClose?: () => void;
  className?: string;
  onShowInScene?: (sceneId: string, issue: ContinuityIssue) => void;
}

type IssueKind = 'pronoun' | 'timeline' | 'character' | 'plot' | 'engagement';

const TYPE_LABELS: Record<IssueKind, string> = {
  pronoun: 'Pronoun',
  timeline: 'Timeline',
  character: 'Character',
  plot: 'Plot',
  engagement: 'Engagement',
} as const;

function IssuePanel(props: IssuePanelProps) {
  const { isOpen = true, onClose, className, onShowInScene } = props;

  // Collapsed placeholder (parent manages layout space)
  if (isOpen === false) {
    return <div className={className} />;
  }

  const manuscript = useManuscriptStore((s) => s.manuscript);

  const {
    analyzeMovedScenes,
    getSceneIssues,
    getIssueCountsByType,
    clearIssues,
    toggleIssueType,
    isAnalyzing,
    progress,
    selectedIssueTypes,
  } = useAnalysis();

  // Moved scenes (ids and map for context)
  const movedScenes = useMemo(() => {
    const scenes = manuscript?.scenes ?? [];
    return scenes.filter((s) => s.hasBeenMoved === true);
  }, [manuscript]);

  const movedSceneIds = useMemo(() => movedScenes.map((s) => s.id), [movedScenes]);

  // Counts by type (respects selectedIssueTypes inside hook implementation)
  const counts = useMemo(() => getIssueCountsByType(movedSceneIds), [getIssueCountsByType, movedSceneIds]);

  // Flat list of issues across moved scenes (grouping optional per subtask; provide scene metadata to items)
  const flatIssues = useMemo(() => {
    const items: Array<{ sceneId: string; issue: ContinuityIssue; sceneTitle?: string; scenePosition?: number }> = [];
    for (const scene of movedScenes) {
      const sceneIssues = getSceneIssues(scene.id);
      if (!sceneIssues || sceneIssues.length === 0) continue;
      for (const issue of sceneIssues) {
        items.push({
          sceneId: scene.id,
          issue,
          // Title/name may not exist on Scene type; fall back to id. Position is available.
          sceneTitle: (scene as unknown as { title?: string; name?: string }).title ?? (scene as unknown as { title?: string; name?: string }).name ?? scene.id,
          scenePosition: scene.position,
        });
      }
    }
    return items;
  }, [movedScenes, getSceneIssues]);

  const totalFilteredIssues = useMemo(
    () => Object.values(counts).reduce((acc, n) => acc + n, 0),
    [counts]
  );

  const handleToggle = useCallback(
    (type: IssueKind) => {
      toggleIssueType(type);
    },
    [toggleIssueType]
  );

  const handleAnalyze = useCallback(() => {
    void analyzeMovedScenes();
  }, [analyzeMovedScenes]);

  const handleShowInScene = useCallback(
    (issue: ContinuityIssue, sceneId: string) => {
      if (onShowInScene) onShowInScene(sceneId, issue);
    },
    [onShowInScene]
  );

  const handleDismissIssue = useCallback(
    (issue: ContinuityIssue, sceneId: string) => {
      // Subtask scope: simple dismissal clears all issues for the scene.
      clearIssues(sceneId);
    },
    [clearIssues]
  );

  const Pill = ({ type }: { type: IssueKind }) => {
    const active = selectedIssueTypes.has(type);
    const count = counts[type] ?? 0;
    return (
      <button
        type="button"
        onClick={() => handleToggle(type)}
        className={[
          'text-xs px-2 py-1 rounded-full border transition',
          active
            ? 'bg-indigo-600 text-white border-indigo-600'
            : 'bg-white/60 dark:bg-gray-900/40 text-gray-800 dark:text-gray-200 border-gray-300 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-800',
        ].join(' ')}
        aria-pressed={active}
        aria-label={`${TYPE_LABELS[type]} issues`}
      >
        <span>{TYPE_LABELS[type]}</span>
        <span
          className={[
            'ml-2 inline-flex items-center justify-center min-w-[1.25rem] h-5 px-1 rounded-full',
            active ? 'bg-white/20 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200',
          ].join(' ')}
        >
          {count}
        </span>
      </button>
    );
  };

  return (
    <div className={['w-full rounded-lg border border-gray-200 dark:border-gray-700 bg-white/70 dark:bg-gray-900/40', className].filter(Boolean).join(' ')}>
      {/* Header */}
      <div className="flex items-center justify-between p-3 border-b border-gray-200 dark:border-gray-700">
        <div className="flex items-center gap-2">
          <h2 className="text-sm font-semibold text-gray-900 dark:text-gray-100">Issues Found in Moved Scenes</h2>
        </div>
        <div className="flex items-center gap-2">
          <button
            type="button"
            onClick={handleAnalyze}
            className="text-xs px-3 py-1.5 rounded-md bg-indigo-600 text-white hover:bg-indigo-700 transition"
          >
            Analyze Moved Scenes
          </button>
          {onClose ? (
            <button
              type="button"
              onClick={onClose}
              className="text-xs px-2 py-1 rounded-md border border-gray-300 dark:border-gray-700 text-gray-700 dark:text-gray-200 hover:bg-gray-50 dark:hover:bg-gray-800"
              aria-label="Close issues panel"
              title="Close"
            >
              √ó
            </button>
          ) : null}
        </div>
      </div>

      {/* Progress */}
      <div className="p-3">
        <AnalysisProgress isAnalyzing={isAnalyzing} progress={progress} />
      </div>

      {/* Filters */}
      <div className="px-3 pb-2">
        <div className="flex flex-wrap gap-2">
          {(['pronoun', 'timeline', 'character', 'plot', 'engagement'] as IssueKind[]).map((t) => (
            <Pill key={t} type={t} />
          ))}
        </div>
      </div>

      {/* Body */}
      <div className="p-3">
        {totalFilteredIssues === 0 && !isAnalyzing && progress?.total > 0 ? (
          <div className="text-sm text-gray-600 dark:text-gray-300">No issues found in moved scenes</div>
        ) : (
          <div className="flex flex-col gap-3">
            {flatIssues.map(({ sceneId, issue, sceneTitle, scenePosition }) => (
              <IssueItem
                key={`${sceneId}-${issue.type}-${issue.textSpan?.[0] ?? 0}-${issue.textSpan?.[1] ?? 0}-${issue.description}`}
                issue={issue}
                sceneId={sceneId}
                sceneTitle={sceneTitle}
                scenePosition={scenePosition}
                onShowInScene={handleShowInScene}
                onDismiss={handleDismissIssue}
              />
            ))}
            {flatIssues.length === 0 && isAnalyzing ? (
              <div className="text-sm text-gray-600 dark:text-gray-300">Analyzing moved scenes‚Ä¶</div>
            ) : null}
          </div>
        )}
      </div>
    </div>
  );
}

export default IssuePanel;

import type { Scene, ContinuityIssue, ReaderKnowledge } from '../../../../shared/types';
import AIServiceManager from '../../../../services/ai/AIServiceManager';
import { enrichAnalysisRequest, runAnalysisWithOptionalConsensus } from '../../../../services/ai/consensus/ConsensusAdapter';
import BaseDetector, { LocalDetectionResult } from './BaseDetector';

/**
 * Character continuity detector (Detector 2 - Phase 2):
 * - Local NLP via compromise (safe) + regex fallback
 * - Previous character registry caching and alias handling
 * - Immediate local issues for missing/weak intros and relationship assumptions
 * - Selective AI validation (analysisType "consistency") for adequacy/ambiguity
 */

// ---------- Internal & exported types ----------
export interface NameOccurrence {
  name: string;
  start: number;
  end: number;
  sentenceIndex: number;
  isFull: boolean;
  isFirstOnly: boolean;
  hasApposition: boolean;
  nickname?: string;
}

export interface PreviousRegistry {
  canonical: Set<string>;
  aliases: Map<string, Set<string>>; // key: canonical, value: alias set
}

export interface CharacterDetectionTarget {
  characterName: string;
  aliasNames: string[];
  firstOccurrence: [number, number];
  sentenceText: string;
  context: string;
  prevRegistry: { canonical: string[]; aliases: string[] };
  relationshipTerms: string[];
  sentenceIndex: number;
}

// ---------- Module state ----------
const registryCache: Map<string, PreviousRegistry> = new Map();

// ---------- Small utilities (kept <= 40 lines) ----------
function escapeRegExp(s: string): string {
  return s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

function signature(previous: readonly Scene[]): string {
  return previous.map(s => s.id).join('|');
}

function splitSentences(text: string): { text: string; start: number; end: number }[] {
  const out: { text: string; start: number; end: number }[] = [];
  try {
    const re = /[^.!?]+(?:[.!?]+|$)/g;
    let m: RegExpExecArray | null;
    while ((m = re.exec(text)) !== null) {
      const seg = text.slice(m.index, m.index + m[0].length);
      out.push({ text: seg.trim(), start: m.index, end: m.index + m[0].length });
    }
  } catch {/* noop */}
  if (out.length === 0) out.push({ text, start: 0, end: text.length });
  return out;
}

function toLowerSet(iter: Iterable<string>): Set<string> {
  const s = new Set<string>();
  for (const v of iter) s.add(v.toLowerCase());
  return s;
}

function appositionAfter(text: string, end: number): boolean {
  const seg = text.slice(end, Math.min(text.length, end + 40));
  return /(,\s*(the|a|an)\s+[a-z-]{2,})|(\s+who\s+)/i.test(seg);
}

function contextSnippet(text: string, start: number, end: number, radius = 120): string {
  const s = Math.max(0, start - radius);
  const e = Math.min(text.length, end + radius);
  return text.slice(s, e).trim();
}

function flattenAliases(reg: PreviousRegistry): Set<string> {
  const flat = new Set<string>();
  for (const set of reg.aliases.values()) for (const a of set) flat.add(a);
  return flat;
}

function firstLastTokens(full: string): { first?: string; last?: string } {
  const parts = full.split(/\s+/).filter(Boolean);
  return { first: parts[0], last: parts.length > 1 ? parts[parts.length - 1] : undefined };
}

function titleStripped(name: string): string {
  return name.replace(/\b(?:Dr|Mr|Mrs|Ms|Miss)\.\s*/g, '').trim();
}

const REL_TERMS = [
  'as usual','like before','as she told him','as he told her','back at it','you know',
  'sis','bro','honey','dear','sweetie','buddy','pal'
];

const PRONOUNS = ['he','she','him','her','his','hers','they','them','their','theirs'];

const NAME_FALLBACK_RE = /\b(?:Dr|Mr|Mrs|Ms|Miss)\.\s+[A-Z][a-z]+(?:\s+[A-Z][a-z]+)?|\b([A-Z][a-z]+(?:\s+[A-Z][a-z]+)?)\b/g;

const NAME_STOP = new Set([
  'The','A','An','And','But','Or','If','Then','When','While','After','Before',
  'Monday','Tuesday','Wednesday','Thursday','Friday','Saturday','Sunday',
  'January','February','March','April','May','June','July','August','September','October','November','December'
]);

// ---------- Required exports (testing hooks) ----------
export function buildPreviousRegistry(previousScenes: readonly Scene[]): PreviousRegistry {
  const canonical = new Set<string>();
  const aliases = new Map<string, Set<string>>();
  const addAlias = (canon: string, a: string | undefined) => {
    if (!a) return;
    const key = canon;
    const set = aliases.get(key) ?? new Set<string>();
    set.add(a);
    aliases.set(key, set);
  };

  for (const sc of previousScenes) {
    let m: RegExpExecArray | null;
    const text = sc.text ?? '';
    while ((m = NAME_FALLBACK_RE.exec(text)) !== null) {
      const raw = m[0].trim();
      const nm = raw;
      if (!nm) continue;
      const stripped = titleStripped(nm);
      const tok0 = stripped.split(/\s+/)[0];
      if (!tok0 || NAME_STOP.has(tok0)) continue;
      canonical.add(stripped);
      const { first, last } = firstLastTokens(stripped);
      addAlias(stripped, first);
      addAlias(stripped, last);
    }
    // nickname patterns: First "Nick" Last OR First (Nick) Last
    const nickRe = /\b([A-Z][a-z]+)\s+(?:"([^"]+)"|\(([^)]+)\))\s+([A-Z][a-z]+)\b/g;
    let nm: RegExpExecArray | null;
    while ((nm = nickRe.exec(text)) !== null) {
      const canon = `${nm[1]} ${nm[4]}`;
      canonical.add(canon);
      const nick = (nm[2] ?? nm[3] ?? '').trim();
      if (nick) addAlias(canon, nick);
      addAlias(canon, nm[1]);
      addAlias(canon, nm[4]);
    }
  }
  return { canonical, aliases };
}

export function extractSceneNames(sceneText: string): NameOccurrence[] {
  const sentences = splitSentences(sceneText);
  const seen = new Set<string>();
  const out: NameOccurrence[] = [];
  let m: RegExpExecArray | null;
  while ((m = NAME_FALLBACK_RE.exec(sceneText)) !== null) {
    const raw = m[0].trim();
    const nm = raw;
    if (!nm) continue;
    const stripped = titleStripped(nm);
    const tok0 = stripped.split(/\s+/)[0];
    if (!tok0 || NAME_STOP.has(tok0)) continue;
    if (seen.has(stripped)) continue;
    seen.add(stripped);
    const start = m.index;
    const end = start + m[0].length;
    let si = sentences.findIndex(s => start >= s.start && start < s.end);
    if (si < 0) si = 0;
    const isFull = /\s/.test(stripped);
    const isFirstOnly = !isFull;
    out.push({ name: stripped, start, end, sentenceIndex: si, isFull, isFirstOnly, hasApposition: appositionAfter(sceneText, end) });
  }
  return out;
}

export function findFirstMentionOffsets(name: string, sceneText: string, fromIndex?: number): [number, number] {
  const nm = escapeRegExp(name);
  const re = new RegExp(`\\b${nm}\\b`);
  const idx = re.exec(sceneText.slice(fromIndex ?? 0))?.index;
  if (idx === undefined) return [-1, -1];
  const start = (fromIndex ?? 0) + idx;
  return [start, start + name.length];
}

export function detectRelationshipTerms(sentenceText: string): string[] {
  const low = sentenceText.toLowerCase();
  const found: string[] = [];
  for (const t of REL_TERMS) {
    if (low.includes(t)) found.push(t);
  }
  return found;
}

export function prepareDetectionTargets(
  names: readonly NameOccurrence[],
  sceneText: string,
  sentences: readonly { text: string; start: number; end: number }[],
  reg: PreviousRegistry
): CharacterDetectionTarget[] {
  const cset = toLowerSet(reg.canonical);
  const aset = toLowerSet(flattenAliases(reg));
  const targets: CharacterDetectionTarget[] = [];
  for (const n of names) {
    const low = n.name.toLowerCase();
    const known = n.isFull ? cset.has(low) : aset.has(low);
    if (!known && (n.isFirstOnly || n.nickname) && !n.hasApposition) {
      const sent = sentences[n.sentenceIndex]?.text ?? '';
      targets.push({
        characterName: n.name,
        aliasNames: Array.from(new Set([firstLastTokens(n.name).first, firstLastTokens(n.name).last, n.nickname].filter(Boolean) as string[])),
        firstOccurrence: [n.start, n.end],
        sentenceText: sent,
        context: contextSnippet(sceneText, n.start, n.end),
        prevRegistry: {
          canonical: Array.from(reg.canonical).slice(0, 6),
          aliases: Array.from(flattenAliases(reg)).slice(0, 6),
        },
        relationshipTerms: detectRelationshipTerms(sent),
        sentenceIndex: n.sentenceIndex,
      });
    }
  }
  return targets;
}

// ---------- Internal helpers for local analysis ----------
function getOrBuildRegistry(previous: readonly Scene[]): PreviousRegistry {
  const sig = signature(previous);
  const cached = registryCache.get(sig);
  if (cached) return cached;
  const reg = buildPreviousRegistry(previous);
  registryCache.set(sig, reg);
  return reg;
}

function compileCurrentNamesWithCompromise(sceneText: string, doc: any | null): NameOccurrence[] {
  if (!doc || typeof doc.people !== 'function') return extractSceneNames(sceneText);
  try {
    const arr: string[] = doc.people().out('array') ?? [];
    const uniq = Array.from(new Set(arr.filter(s => s && typeof s === 'string').map(s => s.trim()).filter(Boolean)));
    const sentences = splitSentences(sceneText);
    const out: NameOccurrence[] = [];
    for (const nm of uniq) {
      const [start, end] = findFirstMentionOffsets(nm, sceneText);
      if (start < 0) continue;
      let si = sentences.findIndex(s => start >= s.start && start < s.end);
      if (si < 0) si = 0;
      const stripped = titleStripped(nm);
      const isFull = /\s/.test(stripped);
      out.push({
        name: stripped,
        start,
        end,
        sentenceIndex: si,
        isFull,
        isFirstOnly: !isFull,
        hasApposition: appositionAfter(sceneText, end),
      });
    }
    return out.length ? out : extractSceneNames(sceneText);
  } catch {
    return extractSceneNames(sceneText);
  }
}

function prevTextContainsRelation(previous: readonly Scene[], name: string, term: string): boolean {
  const hay = previous.map(s => s.text || '').join('\n').toLowerCase();
  return hay.includes(name.toLowerCase()) && hay.includes(term.toLowerCase());
}

function findOpeningPronouns(sceneText: string, sentences: readonly { start: number; end: number }[], maxSentence = 2) {
  const limitEnd = sentences[Math.min(maxSentence - 1, sentences.length - 1)]?.end ?? Math.min(sceneText.length, 240);
  const text = sceneText.slice(0, limitEnd);
  const re = new RegExp(`\\b(${PRONOUNS.join('|')})\\b`, 'gi');
  const list: { pronoun: string; start: number; end: number }[] = [];
  let m: RegExpExecArray | null;
  while ((m = re.exec(text)) !== null) {
    const s = m.index;
    list.push({ pronoun: m[0].toLowerCase(), start: s, end: s + m[0].length });
  }
  return list;
}

// ---------- AI request/response helpers ----------
function buildReaderContext(names: readonly string[]): ReaderKnowledge {
  return {
    knownCharacters: new Set(names),
    establishedTimeline: [],
    revealedPlotPoints: [],
    establishedSettings: [],
  };
}

function registryKnownNames(reg: PreviousRegistry): string[] {
  return Array.from(reg.canonical);
}

function buildAIHeader(scene: Scene, targets: readonly CharacterDetectionTarget[], reg: PreviousRegistry): string {
  const lines: string[] = [];
  lines.push('[[Character detection targets]]');
  lines.push(`scene: id=${scene.id} pos=${scene.position}`);
  const chosen = targets.slice(0, 8);
  for (const c of chosen) {
    const sent = (c.sentenceText || '').slice(0, 140).replace(/\n+/g, ' ');
    const aliases = c.aliasNames.slice(0, 4).join(', ') || 'none';
    const rel = c.relationshipTerms.slice(0, 4).join(', ') || 'none';
    lines.push(`- name="${c.characterName}" span=[${c.firstOccurrence[0]},${c.firstOccurrence[1]}] sent="${sent}" aliases=[${aliases}] rel=[${rel}]`);
  }
  lines.push('[[Previous registry summary]]');
  lines.push(`canonical: ${Array.from(reg.canonical).slice(0, 8).join(' | ')}`);
  lines.push(`aliases: ${Array.from(flattenAliases(reg)).slice(0, 10).join(' | ')}`);
  lines.push('[[Scene excerpt]]');
  return lines.join('\n');
}

function buildSceneExcerpt(text: string, targets: readonly CharacterDetectionTarget[], maxLen = 1200): string {
  if (!targets.length) return text.slice(0, maxLen);
  const snippets: string[] = [];
  const used: [number, number][] = [];
  for (const c of targets) {
    const [s, e] = c.firstOccurrence;
    const snip = contextSnippet(text, s, e, 220);
    if (!snip) continue;
    snippets.push(snip);
    used.push([s, e]);
    const len = snippets.join('\n---\n').length;
    if (len > maxLen) break;
  }
  const out = snippets.join('\n---\n');
  return out.slice(0, maxLen);
}

function mapAICharacterIssues(
  resp: { issues?: ContinuityIssue[] } | null | undefined,
  sceneText: string,
  targets: readonly CharacterDetectionTarget[]
): ContinuityIssue[] {
  const byName = new Map<string, CharacterDetectionTarget>();
  for (const c of targets) byName.set(c.characterName.toLowerCase(), c);
  const out: ContinuityIssue[] = [];
  for (const it of resp?.issues ?? []) {
    if ((it.type ?? 'character') !== 'character') continue;
    const hasSpan = Array.isArray(it.textSpan) && Number.isFinite(it.textSpan[0]) && Number.isFinite(it.textSpan[1]);
    if (hasSpan) {
      out.push({
        type: 'character',
        severity: it.severity ?? 'should-fix',
        description: it.description ?? 'Character introduction/relationship adequacy issue',
        textSpan: it.textSpan as [number, number],
        suggestedFix: it.suggestedFix,
      });
      continue;
    }
    // Fallback: attach to detection target first occurrence by name if present
    const key = (it.description ?? '').toLowerCase();
    let chosen = null as CharacterDetectionTarget | null;
    for (const [nm, c] of byName) {
      if (key.includes(nm)) { chosen = c; break; }
    }
    chosen ??= targets[0] ?? null;
    const span: [number, number] = chosen ? chosen.firstOccurrence : [0, Math.min(1, sceneText.length)];
    out.push({
      type: 'character',
      severity: it.severity ?? 'should-fix',
      description: it.description ?? `Character adequacy issue: ${chosen?.characterName ?? 'unknown'}`,
      textSpan: span,
      suggestedFix: it.suggestedFix,
    });
  }
  return out;
}

// ---------- Local assessment helpers (<= 40 lines each) ----------
function assessFirstAppearanceIssues(
  currentNames: readonly NameOccurrence[],
  reg: PreviousRegistry
): { issues: ContinuityIssue[]; unknownFirstMentions: number } {
  const issues: ContinuityIssue[] = [];
  let count = 0;
  const canSet = toLowerSet(reg.canonical);
  const aliSet = toLowerSet(flattenAliases(reg));
  for (const n of currentNames) {
    const low = n.name.toLowerCase();
    const known = n.isFull ? canSet.has(low) : aliSet.has(low);
    if (!known && (n.isFirstOnly || n.nickname) && !n.hasApposition) {
      count++;
      issues.push({
        type: 'character',
        severity: n.sentenceIndex <= 1 ? 'must-fix' : 'should-fix',
        description: `First mention "${n.name}" appears without introduction/background.`,
        textSpan: [n.start, n.end],
      });
    }
  }
  return { issues, unknownFirstMentions: count };
}

function assessRelationshipAssumptions(
  sentences: readonly { text: string; start: number; end: number }[],
  currentNames: readonly NameOccurrence[],
  previousScenes: readonly Scene[]
): { issues: ContinuityIssue[]; relTermsFound: number } {
  const issues: ContinuityIssue[] = [];
  let relTermsFound = 0;
  for (const sent of sentences) {
    const terms = detectRelationshipTerms(sent.text);
    if (!terms.length) continue;
    const anyNameHere = currentNames.some(n => n.start >= sent.start && n.start < sent.end);
    if (!anyNameHere) continue;
    relTermsFound += terms.length;
    const nameInSent = currentNames.find(n => n.start >= sent.start && n.start < sent.end)?.name;
    const supported = nameInSent ? terms.some(t => prevTextContainsRelation(previousScenes, nameInSent, t)) : false;
    if (!supported) {
      const t0 = terms[0];
      const off = sent.text.toLowerCase().indexOf(t0);
      const begin = off >= 0 ? sent.start + off : sent.start;
      const end = off >= 0 ? begin + t0.length : Math.min(sent.end, begin + 4);
      issues.push({
        type: 'character',
        severity: 'should-fix',
        description: `Relationship assumption "${t0}" with ${nameInSent ?? 'a character'} may lack prior support.`,
        textSpan: [begin, end],
      });
    }
  }
  return { issues, relTermsFound };
}

function assessPronounBeforeNaming(
  sceneText: string,
  sentences: readonly { start: number; end: number }[],
  currentNames: readonly NameOccurrence[],
  reg: PreviousRegistry
): ContinuityIssue[] {
  const issues: ContinuityIssue[] = [];
  const canSet = toLowerSet(reg.canonical);
  const aliSet = toLowerSet(flattenAliases(reg));
  const openingPronouns = findOpeningPronouns(sceneText, sentences, 2);
  if (!openingPronouns.length) return issues;
  const earliestUnknown = currentNames
    .filter(n => {
      const low = n.name.toLowerCase();
      const known = n.isFull ? canSet.has(low) : aliSet.has(low);
      return !known;
    })
    .sort((a, b) => a.sentenceIndex - b.sentenceIndex)[0];
  if (earliestUnknown && earliestUnknown.sentenceIndex >= 2) {
    const p0 = openingPronouns[0];
    issues.push({
      type: 'character',
      severity: 'should-fix',
      description: `Early pronoun reference precedes first naming of a new character "${earliestUnknown.name}".`,
      textSpan: [p0.start, p0.end],
    });
  }
  return issues;
}
// ---------- Detector implementation ----------
export default class CharacterDetector extends BaseDetector<CharacterDetectionTarget> {
  public readonly detectorType = 'character' as const;

  protected async localDetection(
    scene: Scene,
    previousScenes: readonly Scene[],
    _aiManager: AIServiceManager
  ): Promise<LocalDetectionResult<CharacterDetectionTarget>> {
    if (!scene?.text || typeof scene.text !== 'string' || scene.text.trim().length === 0) {
      return { issues: [], requiresAI: false, targets: [] };
    }

    const doc = await this.safeNLP(scene.text);
    const sentences = splitSentences(scene.text);
    const currentNames = compileCurrentNamesWithCompromise(scene.text, doc);
    const reg = getOrBuildRegistry(previousScenes);
    const canSet = toLowerSet(reg.canonical);
    const aliSet = toLowerSet(flattenAliases(reg));

    let unknownFirstMentions = 0;
    let relTermsFound = 0;
    const issues: ContinuityIssue[] = [];

    // First-appearance without intro
    for (const n of currentNames) {
      const low = n.name.toLowerCase();
      const known = n.isFull ? canSet.has(low) : aliSet.has(low);
      if (!known && (n.isFirstOnly || n.nickname) && !n.hasApposition) {
        unknownFirstMentions++;
        const sev: ContinuityIssue['severity'] = n.sentenceIndex <= 1 ? 'must-fix' : 'should-fix';
        issues.push({
          type: 'character',
          severity: sev,
          description: `First mention "${n.name}" appears without introduction/background.`,
          textSpan: [n.start, n.end],
        });
      }
    }

    // Relationship assumptions
    for (const sent of sentences) {
      const terms = detectRelationshipTerms(sent.text);
      if (!terms.length) continue;
      const anyNameHere = currentNames.some(n => n.start >= sent.start && n.start < sent.end);
      if (!anyNameHere) continue;
      relTermsFound += terms.length;
      // If no evidence in previous scenes, flag should-fix
      const nameInSent = currentNames.find(n => n.start >= sent.start && n.start < sent.end)?.name;
      const supported = nameInSent ? terms.some(t => prevTextContainsRelation(previousScenes, nameInSent, t)) : false;
      if (!supported) {
        const t0 = terms[0];
        const off = sent.text.toLowerCase().indexOf(t0);
        const begin = off >= 0 ? sent.start + off : sent.start;
        const end = off >= 0 ? begin + t0.length : Math.min(sent.end, begin + 4);
        issues.push({
          type: 'character',
          severity: 'should-fix',
          description: `Relationship assumption "${t0}" with ${nameInSent ?? 'a character'} may lack prior support.`,
          textSpan: [begin, end],
        });
      }
    }

    // Pronouns before naming (approximate)
    const openingPronouns = findOpeningPronouns(scene.text, sentences, 2);
    if (openingPronouns.length) {
      const earliestUnknown = currentNames
        .filter(n => {
          const low = n.name.toLowerCase();
          const known = n.isFull ? canSet.has(low) : aliSet.has(low);
          return !known;
        })
        .sort((a, b) => a.sentenceIndex - b.sentenceIndex)[0];
      if (earliestUnknown && earliestUnknown.sentenceIndex >= 2) {
        const p0 = openingPronouns[0];
        issues.push({
          type: 'character',
          severity: 'should-fix',
          description: `Early pronoun reference precedes first naming of a new character "${earliestUnknown.name}".`,
          textSpan: [p0.start, p0.end],
        });
      }
    }

    const targets = prepareDetectionTargets(currentNames, scene.text, sentences, reg);
    console.debug('[CharacterDetector] names:', currentNames.length, 'unknownFirst:', unknownFirstMentions, 'relTerms:', relTermsFound, 'targets:', targets.length);

    return {
      issues,
      requiresAI: targets.length > 0,
      targets,
      stats: {
        namesFound: currentNames.length,
        unknownFirstMentions,
        relTermsFound,
        targets: targets.length,
      },
    };
  }

  protected async aiDetection(
    scene: Scene,
    previousScenes: readonly Scene[],
    aiManager: AIServiceManager,
    targets: readonly CharacterDetectionTarget[]
  ): Promise<ContinuityIssue[]> {
    if (!targets || targets.length === 0) return [];
    try {
      const reg = getOrBuildRegistry(previousScenes);
      const header = buildAIHeader(scene, targets, reg);
      const excerpt = buildSceneExcerpt(scene.text, targets, 1200);
      const lastPrev = previousScenes.slice(-1).map(s => ({ ...s, text: (s.text ?? '').slice(0, 800) }));
      const baseReq = {
        scene: { ...scene, text: `${header}\n\n${excerpt}` },
        previousScenes: lastPrev as Scene[],
        analysisType: 'consistency' as const,
        readerContext: buildReaderContext(registryKnownNames(reg)),
      } as Parameters<AIServiceManager['analyzeContinuity']>[0];

      const enriched = enrichAnalysisRequest(baseReq as any, {
        scene,
        detectorType: 'character',
        flags: { critical: Boolean((scene as any)?.critical) },
      });

      console.debug('[CharacterDetector] invoking AI (consistency) for targets:', targets.length);
      const { issues } = await runAnalysisWithOptionalConsensus(aiManager, enriched as any, {
        critical: Boolean((enriched as any)?.flags?.critical),
        consensusCount: 2,
        acceptThreshold: 0.5,
        humanReviewThreshold: 0.9,
        maxModels: 2,
      });

      const out = mapAICharacterIssues({ issues }, scene.text, targets);
      console.debug('[CharacterDetector] AI returned character issues:', out.length);
      return out;
    } catch (err) {
      console.debug('[CharacterDetector] AI analyzeContinuity failed; degrading to local-only.', err);
      return [];
    }
  }
}

import type { Scene, ContinuityIssue } from '../../../../shared/types';
import AIServiceManager from '../../../../services/ai/AIServiceManager';
import { enrichAnalysisRequest, runAnalysisWithOptionalConsensus } from '../../../../services/ai/consensus/ConsensusAdapter';
import BaseDetector, { LocalDetectionResult } from './BaseDetector';

interface OpeningStats {
  tokenCount150: number;
  avgSentenceLen: number;
  dialogueRatio: number;
  namedEntities: number;
  adverbs: number;
  adjectives: number;
  uniqueProperNouns2: number;
}

interface PreviousSummary {
  characters: string[];
  keyTerms: string[];
}

interface EngagementDetectionTarget {
  hookLine: string;
  firstSentences: string[];
  contextWindow: string;
  openingStats: OpeningStats;
  previousSummary: PreviousSummary;
}

const ADJ_WORDS = new Set([
  'new','young','old','small','large','little','long','short','great','good','bad','happy','sad','dark','bright','cold','warm','quiet','loud','sudden','slow','fast','early'
]);

export function splitSentences(text: string): { start: number; end: number; text: string }[] {
  const out: { start: number; end: number; text: string }[] = [];
  try {
    const re = /[^.!?]+(?:[.!?]+|$)/g;
    let m: RegExpExecArray | null;
    while ((m = re.exec(text)) !== null) {
      const seg = text.slice(m.index, m.index + m[0].length);
      out.push({ start: m.index, end: m.index + m[0].length, text: seg.trim() });
    }
  } catch { /* noop */ }
  if (out.length === 0) out.push({ start: 0, end: text.length, text });
  return out;
}

export function extractHookLine(text: string): string {
  const sents = splitSentences(text);
  const first = sents.find(s => s.text.trim().length > 0)?.text ?? '';
  return first.trim().slice(0, 200);
}

function uniqueProperInSentences(text: string, sents: readonly { start: number; end: number }[], count = 2): number {
  const take = sents.slice(0, Math.max(0, Math.min(count, sents.length)));
  const set = new Set<string>();
  for (const s of take) {
    const seg = text.slice(s.start, s.end);
    const m = seg.match(/\b[A-Z][a-z]+\b/g) ?? [];
    m.forEach((t, idx) => { if (idx > 0) set.add(t); });
  }
  return set.size;
}

export function computeOpeningStats(
  text: string,
  firstSentenceSpans: readonly { start: number; end: number; text: string }[],
  nlpDocOrNull: any | null
): OpeningStats {
  const words = (text || '').trim().split(/\s+/).filter(Boolean);
  const head = words.slice(0, 150).join(' ');
  const tokenCount150 = Math.min(words.length, 150);
  const avgSentenceLen = (() => {
    const consider = firstSentenceSpans.slice(0, Math.min(3, firstSentenceSpans.length));
    if (!consider.length) return 0;
    const totals = consider.map(s => (s.text.split(/\s+/).filter(Boolean).length));
    return Math.round((totals.reduce((a, b) => a + b, 0) / consider.length) * 10) / 10;
  })();
  const dialogueRatio = (() => {
    const lines = head.split(/\r?\n/).filter(l => l.trim().length > 0);
    if (!lines.length) return 0;
    const dlg = lines.filter(l => /^["‚Äú]/.test(l.trim()) || /\bsaid\b/i.test(l) || /\.\.\./.test(l));
    return Math.round((dlg.length / lines.length) * 100) / 100;
  })();
  const namedEntities = (() => {
    try { return Array.isArray(nlpDocOrNull?.people?.().out?.('array')) ? (nlpDocOrNull.people().out('array') as string[]).length : (head.match(/\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)+\b/g) ?? []).length; }
    catch { return (head.match(/\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)+\b/g) ?? []).length; }
  })();
  const adverbs = (head.match(/\b\w+ly\b/g) ?? []).length;
  const adjectives = (() => {
    try { const n = nlpDocOrNull?.adjectives?.()?.out?.('array'); if (Array.isArray(n)) return n.length; } catch {/*noop*/}
    const toks = head.toLowerCase().match(/\b[a-z]{3,}\b/g) ?? [];
    return toks.reduce((c, w) => c + (ADJ_WORDS.has(w) ? 1 : 0), 0);
  })();
  const uniqueProperNouns2 = uniqueProperInSentences(text, firstSentenceSpans, 2);
  return { tokenCount150, avgSentenceLen, dialogueRatio, namedEntities, adverbs, adjectives, uniqueProperNouns2 };
}

export function summarizePreviousContext(previousScenes: readonly Scene[]): PreviousSummary {
  const joined = previousScenes.map(s => s.text || '').join('\n').slice(0, 5000);
  const characters = (() => {
    try {
      // Attempt compromise if available
       
      const mod = require('compromise');
      const nlp = (mod?.default ?? mod) as any;
      const doc = typeof nlp === 'function' ? nlp(joined) : null;
      const arr: string[] = doc?.people?.()?.out?.('array') ?? [];
      const uniq = Array.from(new Set(arr.map(s => String(s).trim()).filter(Boolean)));
      return uniq.slice(0, 6);
    } catch {
      const m = joined.match(/\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)+\b/g) ?? [];
      const uniq = Array.from(new Set(m));
      return uniq.slice(0, 6);
    }
  })();
  const keyTerms = (() => {
    const caps = (joined.match(/\b[A-Z][a-z]+\b/g) ?? []).map(s => s.trim());
    const stop = new Set(['The','A','An','And','But','Or','If','Then','When','While','After','Before']);
    const freq = new Map<string, number>();
    for (const t of caps) {
      if (stop.has(t)) continue;
      freq.set(t, (freq.get(t) ?? 0) + 1);
    }
    return Array.from(freq.entries()).sort((a,b)=>b[1]-a[1]).slice(0,5).map(e=>e[0]);
  })();
  return { characters, keyTerms };
}

export function buildEngagementDetectionTarget(
  scene: Scene,
  previousScenes: readonly Scene[],
  stats: OpeningStats,
  sentences: readonly { start: number; end: number; text: string }[]
): EngagementDetectionTarget {
  const hookLine = extractHookLine(scene.text || '');
  const firstSentences = sentences.slice(0, 3).map(s => s.text.trim());
  const contextWindow = (scene.text || '').slice(0, 900);
  const previousSummary = summarizePreviousContext(previousScenes);
  return { hookLine, firstSentences, contextWindow, openingStats: stats, previousSummary };
}

function findHookSpan(sceneText: string, hookLine: string): [number, number] {
  const trimmed = (hookLine || '').trim();
  if (!trimmed) return [0, Math.min(sceneText.length, 1)];
  const idx = sceneText.indexOf(trimmed);
  if (idx >= 0) return [idx, idx + trimmed.length];
  return [0, Math.min(sceneText.length, Math.max(1, trimmed.length))];
}

function deriveSeverity(desc?: string): ContinuityIssue['severity'] {
  const d = (desc || '').toLowerCase();
  if (/(no|weak)\s+hook|no\s+conflict|los(e|ing)\s+reader|very\s+slow\s+pacing/.test(d)) return 'must-fix';
  if (/moderate|somewhat\s+slow|dense\s+exposition|confusing\s+load|could\s+be\s+tighter/.test(d)) return 'should-fix';
  return 'consider';
}

function mapAIEngagementIssues(
  resp: { issues?: ContinuityIssue[] } | null | undefined,
  sceneText: string,
  hookLine: string
): ContinuityIssue[] {
  const out: ContinuityIssue[] = [];
  const fallbackSpan = findHookSpan(sceneText, hookLine);
  for (const it of resp?.issues ?? []) {
    const hasSpan = Array.isArray(it.textSpan) && Number.isFinite(it.textSpan[0]) && Number.isFinite(it.textSpan[1]);
    out.push({
      type: 'engagement',
      severity: (it.severity as any) || deriveSeverity(it.description),
      description: it.description ?? 'Engagement issue detected in opening.',
      textSpan: (hasSpan ? (it.textSpan as [number, number]) : fallbackSpan),
      suggestedFix: it.suggestedFix
    });
  }
  return out;
}

function buildReaderContextFromSummary(sum: PreviousSummary) {
  return {
    knownCharacters: new Set(sum.characters),
    establishedTimeline: [],
    revealedPlotPoints: [],
    establishedSettings: []
  };
}

function buildAIHeader(scene: Scene, cand: EngagementDetectionTarget): string {
  const s = cand.openingStats;
  const parts: string[] = [];
  parts.push('[[Engagement assessment request]]');
  parts.push(`scene: id=${scene.id} pos=${scene.position}`);
  parts.push(`hook: "${cand.hookLine.slice(0, 160).replace(/\n+/g, ' ')}"`);
  parts.push(`openingStats: token150=${s.tokenCount150} avgSentLen=${s.avgSentenceLen} dialogRatio=${s.dialogueRatio} ents=${s.namedEntities} adv=${s.adverbs} adj=${s.adjectives} proper2=${s.uniqueProperNouns2}`);
  parts.push('Assess:');
  parts.push('- Opening hook clarity/strength');
  parts.push('- Early tension/conflict presence');
  parts.push('- Pacing in first ~700‚Äì900 chars');
  parts.push('- Character introduction load and clarity');
  const chars = cand.previousSummary.characters.join(', ');
  const terms = cand.previousSummary.keyTerms.join(', ');
  parts.push(`prevContext: chars=[${chars}] key=[${terms}]`);
  return parts.join('\n');
}

export default class EngagementDetector extends BaseDetector<EngagementDetectionTarget> {
  public readonly detectorType = 'engagement' as const;

  protected async localDetection(
    scene: Scene,
    previousScenes: readonly Scene[],
    _aiManager: AIServiceManager
  ): Promise<LocalDetectionResult<EngagementDetectionTarget>> {
    if (!scene?.text || scene.text.trim().length === 0) {
      return { issues: [], requiresAI: false, targets: [] };
    }
    const doc = await this.safeNLP(scene.text);
    const sentences = splitSentences(scene.text);
    const stats = computeOpeningStats(scene.text, sentences, doc);
    const target = buildEngagementDetectionTarget(scene, previousScenes, stats, sentences);
    const targets = target.hookLine ? [target] : [];
    console.debug('[EngagementDetector] stats/target:', { stats, hasTarget: targets.length > 0 });
    return {
      issues: [],
      requiresAI: targets.length > 0,
      targets,
      stats: stats as unknown as Record<string, number>
    };
  }

  protected async aiDetection(
    scene: Scene,
    previousScenes: readonly Scene[],
    aiManager: AIServiceManager,
    targets: readonly EngagementDetectionTarget[]
  ): Promise<ContinuityIssue[]> {
    if (!targets || targets.length === 0) return [];
    const cand = targets[0];
    try {
      const header = buildAIHeader(scene, cand);
      const body: string[] = [];
      body.push('[[First sentences]]');
      for (const s of cand.firstSentences) body.push(`- ${s}`);
      body.push('[[Scene context (~900 chars)]]');
      body.push(cand.contextWindow);
      const textPayload = `${header}\n\n${body.join('\n')}`;
      const lastPrev = previousScenes.slice(-1).map(s => ({ ...s, text: (s.text ?? '').slice(0, 600) }));
      const baseReq = {
        scene: { ...scene, text: textPayload },
        previousScenes: lastPrev as Scene[],
        analysisType: 'full' as const,
        readerContext: buildReaderContextFromSummary(cand.previousSummary)
      } as Parameters<AIServiceManager['analyzeContinuity']>[0];

      const enriched = enrichAnalysisRequest(baseReq as any, {
        scene,
        detectorType: 'engagement',
        flags: { critical: Boolean((scene as any)?.critical) },
      });

      console.debug('[EngagementDetector] invoking AI (full) for detection targets');
      const { issues } = await runAnalysisWithOptionalConsensus(aiManager, enriched as any, {
        critical: Boolean((enriched as any)?.flags?.critical),
        consensusCount: 2,
        acceptThreshold: 0.5,
        humanReviewThreshold: 0.9,
        maxModels: 2,
      });

      const out = mapAIEngagementIssues({ issues }, scene.text, cand.hookLine);
      console.debug('[EngagementDetector] AI returned engagement issues:', out.length);
      return out;
    } catch (err) {
      console.debug('[EngagementDetector] AI analyzeContinuity failed; returning empty.', err);
      return [];
    }
  }
}

import type { Scene, ContinuityIssue, ReaderKnowledge } from '../../../../shared/types';
import AIServiceManager from '../../../../services/ai/AIServiceManager';
import { enrichAnalysisRequest, runAnalysisWithOptionalConsensus } from '../../../../services/ai/consensus/ConsensusAdapter';
import BaseDetector, { LocalDetectionResult } from './BaseDetector';

// ---------- Types (exported testing hooks) ----------
export interface SentenceSpan { text: string; start: number; end: number }

export interface BasicMarker {
  text: string;
  start: number;
  end: number;
  sentenceIndex: number;
}

type MarkerCategory =
  | 'relative'
  | 'absolute'
  | 'time'
  | 'season'
  | 'weekday'
  | 'holiday'
  | 'sequence'
  | 'month';

interface TemporalMarker extends BasicMarker {
  category: MarkerCategory;
  normalized?: string;
  deltaDays?: number | null;
  anchor?: 'morning' | 'afternoon' | 'evening' | 'night' | 'day';
}

export interface PreviousTimelineRegistry {
  lastAnchor: string | null;
  lastDayOffset: number | null; // approximate cumulative day offset
  seasons: Set<string>; // e.g., winter, summer
  months: Set<string>; // e.g., january
  recentSequences: string[]; // e.g., then, after that
  hasMeanwhile: boolean;
}

export interface TimelineDetectionTarget {
  markerText: string;
  sentenceText: string;
  context: string;
  start: number;
  end: number;
  prevSummary: { lastAnchor: string | null; lastDayOffset: number | null; seasons: string[]; months: string[] };
  otherMarkers: string[];
  sentenceIndex: number;
}

// ---------- Constants ----------
const MONTHS = [
  'january','february','march','april','may','june','july','august','september','october','november','december'
];
const WEEKDAYS = ['monday','tuesday','wednesday','thursday','friday','saturday','sunday'];
const SEASONS = ['spring','summer','fall','autumn','winter'];
const HOLIDAYS = ['christmas','new year','new year\'s','easter','thanksgiving','halloween','hanukkah','ramadan','diwali'];
const TIME_OF_DAY = ['dawn','morning','noon','afternoon','dusk','evening','midnight','night','tonight'];
const SEQUENCE_TERMS = ['then','after that','later that day','soon after','meanwhile','at the same time','moments later'];

const RELATIVE_TERMS = [
  'next day','the following day','tomorrow','yesterday','earlier that day','that night','tonight',
  'the previous night','next morning','this morning','last night','later that day'
];

const WEATHER_SEASONAL = ['snow','blizzard','sleet','heatwave','scorching','sweltering','icy','frost','hail'];

// ---------- Module cache ----------
const registryCache: Map<string, PreviousTimelineRegistry> = new Map();

// ---------- Small utilities (&#x2264; 40 lines) ----------
function sig(previous: readonly Scene[]): string {
  return previous.map(s => s.id).join('|');
}

function clamp(n: number, a: number, b: number): number {
  return Math.max(a, Math.min(b, n));
}

function snippet(text: string, start: number, end: number, r = 120): string {
  const s = clamp(start - r, 0, text.length);
  const e = clamp(end + r, 0, text.length);
  return text.slice(s, e).trim();
}

function monthToSeason(month: string): string | null {
  const m = month.toLowerCase();
  if (['december','january','february'].includes(m)) return 'winter';
  if (['march','april','may'].includes(m)) return 'spring';
  if (['june','july','august'].includes(m)) return 'summer';
  if (['september','october','november'].includes(m)) return 'fall';
  return null;
}

export function computeRelativeDelta(phrase: string): number | null {
  const low = phrase.toLowerCase();
  const numMatch = /(\d+)\s+(day|week|month|year)s?\s+(later|earlier|before|after)/i.exec(low);
  if (numMatch) {
    const n = parseInt(numMatch[1], 10);
    const unit = numMatch[2];
    const dir = numMatch[3];
    const mult = unit === 'day' ? 1 : unit === 'week' ? 7 : unit === 'month' ? 30 : 365;
    const sign = (dir === 'later' || dir === 'after') ? 1 : -1;
    return sign * n * mult;
  }
  if (/\b(next day|the following day|tomorrow|next morning)\b/i.test(low)) return 1;
  if (/\b(previous day|yesterday|the previous night|last night)\b/i.test(low)) return -1;
  if (/\b(earlier that day|that night|tonight|this morning|later that day)\b/i.test(low)) return 0;
  return null;
}

function anchorFromPhrase(phrase: string): TemporalMarker['anchor'] | undefined {
  const low = phrase.toLowerCase();
  if (low.includes('morning') || low.includes('dawn') || low.includes('this morning') || low.includes('next morning')) return 'morning';
  if (low.includes('afternoon') || low.includes('noon')) return 'afternoon';
  if (low.includes('evening') || low.includes('dusk')) return 'evening';
  if (low.includes('night') || low.includes('midnight') || low.includes('tonight') || low.includes('last night')) return 'night';
  return undefined;
}

export function splitSentences(text: string): SentenceSpan[] {
  const spans: SentenceSpan[] = [];
  try {
    const re = /[^.!?]+(?:[.!?]+|\n+|$)/g;
    let m: RegExpExecArray | null;
    while ((m = re.exec(text)) !== null) {
      const raw = text.slice(m.index, m.index + m[0].length);
      spans.push({ text: raw.trim(), start: m.index, end: m.index + m[0].length });
    }
  } catch { /* noop */ }
  if (spans.length === 0) spans.push({ text: text, start: 0, end: text.length });
  return spans;
}

function sentenceIndexFor(pos: number, sentences: readonly SentenceSpan[]): number {
  let lo = 0, hi = sentences.length - 1, ans = 0;
  while (lo <= hi) {
    const mid = (lo + hi) >> 1;
    const s = sentences[mid];
    if (pos < s.start) hi = mid - 1;
    else if (pos >= s.end) lo = mid + 1;
    else { ans = mid; break; }
  }
  return ans;
}

// ---------- Marker extraction (compromise + regex) ----------
function pushMatch(
  arr: TemporalMarker[],
  text: string,
  start: number,
  end: number,
  sentences: readonly SentenceSpan[],
  category: MarkerCategory
): void {
  const si = sentenceIndexFor(start, sentences);
  const phrase = text.slice(start, end);
  arr.push({
    text: phrase,
    start,
    end,
    sentenceIndex: si,
    category,
    deltaDays: category === 'relative' ? computeRelativeDelta(phrase) : null,
    anchor: category === 'relative' || category === 'time' ? anchorFromPhrase(phrase) : undefined,
  });
}

function regexFindAll(text: string, re: RegExp, sentences: readonly SentenceSpan[], cat: MarkerCategory, out: TemporalMarker[]) {
  let m: RegExpExecArray | null;
  while ((m = re.exec(text)) !== null) {
    const s = m.index;
    const e = s + m[0].length;
    pushMatch(out, text, s, e, sentences, cat);
  }
}

function extractWithCompromise(doc: any | null, text: string, sentences: readonly SentenceSpan[]): TemporalMarker[] {
  const out: TemporalMarker[] = [];
  if (!doc) return out;
  try {
    const toArr = (sel: any): string[] => (sel && typeof sel.out === 'function') ? (sel.out('array') ?? []) : [];
    const datePhrases: string[] = toArr(doc.match ? doc.match('#Date') : null).concat(toArr(doc.dates ? doc.dates() : null));
    const timePhrases: string[] = toArr(doc.match ? doc.match('#Time') : null);
    const seen = new Set<string>();
    const addStrings = (phrases: string[], cat: MarkerCategory) => {
      for (const p of phrases) {
        const phrase = (p || '').toString().trim();
        if (!phrase) continue;
        if (seen.has(`${cat}|${phrase}`)) continue;
        seen.add(`${cat}|${phrase}`);
        // search within each sentence to avoid duping earlier matches
        for (const s of sentences) {
          const relIdx = s.text.indexOf(phrase);
          if (relIdx >= 0) {
            const start = s.start + relIdx;
            const end = start + phrase.length;
            pushMatch(out, text, start, end, sentences, cat);
            break;
          }
        }
      }
    };
    addStrings(datePhrases, 'absolute');
    addStrings(timePhrases, 'time');
    return out;
  } catch {
    return out;
  }
}

export function extractTemporalMarkers(
  sceneText: string,
  doc: any | null = null
): { markers: BasicMarker[]; sentences: SentenceSpan[] } {
  if (!sceneText || typeof sceneText !== 'string') return { markers: [], sentences: [{ text: '', start: 0, end: 0 }] };
  const sentences = splitSentences(sceneText);
  const tmp: TemporalMarker[] = [];
  // Compromise-based
  const comp = extractWithCompromise(doc, sceneText, sentences);
  tmp.push(...comp);
  // Regex-based relative amounts
  regexFindAll(sceneText, /\b(\d+)\s+(?:day|week|month|year)s?\s+(?:later|earlier|before|after)\b/gi, sentences, 'relative', tmp);
  // Simple relative terms
  regexFindAll(sceneText, new RegExp('\\b(?:' + RELATIVE_TERMS.join('|').replace(/ /g, '\\s+') + ')\\b', 'gi'), sentences, 'relative', tmp);
  // Sequence terms
  regexFindAll(sceneText, new RegExp('\\b(?:' + SEQUENCE_TERMS.join('|').replace(/ /g, '\\s+') + ')\\b', 'gi'), sentences, 'sequence', tmp);
  // Absolute months, weekdays, seasons, holidays, time-of-day, weather
  regexFindAll(sceneText, new RegExp('\\b(?:' + MONTHS.join('|') + ')\\b', 'gi'), sentences, 'month', tmp);
  regexFindAll(sceneText, new RegExp('\\b(?:' + WEEKDAYS.join('|') + ')\\b', 'gi'), sentences, 'weekday', tmp);
  regexFindAll(sceneText, new RegExp('\\b(?:' + SEASONS.join('|') + ')\\b', 'gi'), sentences, 'season', tmp);
  regexFindAll(sceneText, new RegExp('\\b(?:' + HOLIDAYS.join('|') + ')\\b', 'gi'), sentences, 'holiday', tmp);
  regexFindAll(sceneText, new RegExp('\\b(?:' + TIME_OF_DAY.join('|') + ')\\b', 'gi'), sentences, 'time', tmp);
  regexFindAll(sceneText, new RegExp('\\b(?:' + WEATHER_SEASONAL.join('|') + ')\\b', 'gi'), sentences, 'season', tmp);
  // Return as BasicMarker list (testing contract)
  const basics: BasicMarker[] = tmp.map(m => ({ text: m.text, start: m.start, end: m.end, sentenceIndex: m.sentenceIndex }));
  return { markers: basics, sentences };
}

// ---------- Previous registry builder + caching ----------
function getOrBuildRegistry(previous: readonly Scene[]): PreviousTimelineRegistry {
  const s = sig(previous);
  const c = registryCache.get(s);
  if (c) return c;
  const reg = buildPreviousTimelineRegistry(previous);
  registryCache.set(s, reg);
  return reg;
}

export function buildPreviousTimelineRegistry(previousScenes: readonly Scene[]): PreviousTimelineRegistry {
  const seasons = new Set<string>();
  const months = new Set<string>();
  const recentSequences: string[] = [];
  let lastAnchor: string | null = null;
  let cumOffset = 0;
  let hasMeanwhile = false;

  for (const sc of previousScenes) {
    const text = sc.text ?? '';
    const sentences = splitSentences(text);
    const markers = extractWithCompromise(null, text, sentences);
    // Regex fallback markers too
    const all: TemporalMarker[] = [];
    all.push(...markers);
    regexFindAll(text, /\b(\d+)\s+(?:day|week|month|year)s?\s+(?:later|earlier|before|after)\b/gi, sentences, 'relative', all);
    regexFindAll(text, new RegExp('\\b(?:' + RELATIVE_TERMS.join('|').replace(/ /g, '\\s+') + ')\\b', 'gi'), sentences, 'relative', all);
    regexFindAll(text, new RegExp('\\b(?:' + SEQUENCE_TERMS.join('|').replace(/ /g, '\\s+') + ')\\b', 'gi'), sentences, 'sequence', all);
    regexFindAll(text, new RegExp('\\b(?:' + MONTHS.join('|') + ')\\b', 'gi'), sentences, 'month', all);
    regexFindAll(text, new RegExp('\\b(?:' + SEASONS.join('|') + ')\\b', 'gi'), sentences, 'season', all);
    for (const m of all) {
      if (m.category === 'relative' && m.deltaDays != null) {
        cumOffset += m.deltaDays;
        if (m.anchor) lastAnchor = m.anchor;
      }
      if (m.category === 'time' && m.anchor) lastAnchor = m.anchor;
      if (m.category === 'season') seasons.add(m.text.toLowerCase().replace('autumn', 'fall'));
      if (m.category === 'month') {
        const mon = m.text.toLowerCase();
        months.add(mon);
        const seas = monthToSeason(mon);
        if (seas) seasons.add(seas);
      }
      if (m.category === 'sequence') {
        const low = m.text.toLowerCase();
        recentSequences.push(low);
        if (low.includes('meanwhile') || low.includes('at the same time')) hasMeanwhile = true;
      }
    }
  }

  return {
    lastAnchor,
    lastDayOffset: Number.isNaN(cumOffset) ? null : cumOffset,
    seasons,
    months,
    recentSequences: recentSequences.slice(-20),
    hasMeanwhile,
  };
}

// ---------- Detection target assembly (exported) ----------
export function assembleTimelineDetectionTargets(
  markers: readonly BasicMarker[],
  sceneText: string,
  sentences: readonly SentenceSpan[],
  reg: PreviousTimelineRegistry
): TimelineDetectionTarget[] {
  const lowMarkers = markers.map(m => ({ ...m, low: m.text.toLowerCase() }));
  const otherTexts = (skipIdx: number) =>
    lowMarkers.filter((_, i) => i !== skipIdx).slice(0, 6).map(mm => mm.text);

  const targets: TimelineDetectionTarget[] = [];
  const hasMeanwhile = lowMarkers.some(m => m.low.includes('meanwhile') || m.low.includes('at the same time'));
  const relativeCount = lowMarkers.filter(m => /\b(day|week|month|year|morning|night|yesterday|tomorrow|earlier|later)\b/.test(m.low)).length;

  for (let i = 0; i < lowMarkers.length; i++) {
    const m = lowMarkers[i];
    const isAmbiguousSeason =
      (SEASONS.some(s => m.low.includes(s)) && reg.months.size > 0) ||
      (MONTHS.some(mon => m.low.includes(mon)) && reg.seasons.size > 0);

    const triggers =
      hasMeanwhile ||
      relativeCount >= 2 ||
      isAmbiguousSeason ||
      /\bearlier that day\b/.test(m.low);

    if (!triggers) continue;

    const sent = sentences[m.sentenceIndex]?.text ?? '';
    targets.push({
      markerText: m.text,
      sentenceText: sent,
      context: snippet(sceneText, m.start, m.end),
      start: m.start,
      end: m.end,
      prevSummary: {
        lastAnchor: reg.lastAnchor,
        lastDayOffset: reg.lastDayOffset,
        seasons: Array.from(reg.seasons).slice(0, 6),
        months: Array.from(reg.months).slice(0, 6),
      },
      otherMarkers: otherTexts(i),
      sentenceIndex: m.sentenceIndex,
    });
  }
  return targets;
}

// ---------- Local conflict detection ----------
function seasonBucketSet(markers: readonly BasicMarker[]): Set<string> {
  const set = new Set<string>();
  for (const m of markers) {
    const low = m.text.toLowerCase();
    for (const s of SEASONS) if (low.includes(s)) set.add(s === 'autumn' ? 'fall' : s);
    for (const mon of MONTHS) if (low.includes(mon)) {
      const sb = monthToSeason(mon); if (sb) set.add(sb);
    }
    for (const w of WEATHER_SEASONAL) if (low.includes(w)) {
      if (['snow','blizzard','sleet','icy','frost','hail'].some(x => low.includes(x))) set.add('winter');
      if (['heatwave','scorching','sweltering'].some(x => low.includes(x))) set.add('summer');
    }
  }
  return set;
}

function detectTimelineIssues(
  markers: readonly BasicMarker[],
  reg: PreviousTimelineRegistry,
  sceneText: string
): ContinuityIssue[] {
  const issues: ContinuityIssue[] = [];
  const lowMarks = markers.map(m => ({ ...m, low: m.text.toLowerCase() }));
  const hasEarlierToday = lowMarks.some(m => m.low.includes('earlier that day'));
  const hasNextMorning = lowMarks.some(m => m.low.includes('next morning'));
  const hasMultiDayJump = lowMarks.some(m => /(\d+)\s+(day|week|month|year)s?\s+(later|after)/.test(m.low));
  const curSeasons = seasonBucketSet(markers);

  // Hard contradiction: "earlier that day" but previous indicates we're past same-day context
  if (hasEarlierToday && (reg.lastDayOffset !== null && reg.lastDayOffset > 0)) {
    const m = lowMarks.find(x => x.low.includes('earlier that day'))!;
    issues.push({
      type: 'timeline',
      severity: 'must-fix',
      description: 'Temporal rewind: "earlier that day" after prior scenes advanced beyond same-day.',
      textSpan: [m.start, m.end],
    });
  }

  // Likely gap: large jump while recent threads suggest simultaneity
  if (reg.hasMeanwhile && hasMultiDayJump) {
    const m = lowMarks.find(x => /(\d+)\s+(day|week|month|year)s?\s+(later|after)/.test(x.low))!;
    issues.push({
      type: 'timeline',
      severity: 'should-fix',
      description: 'Potential gap: multi-day jump despite ongoing "meanwhile" threads in prior scenes.',
      textSpan: [m.start, m.end],
    });
  }

  // Soft contradiction: "next morning" after cumulative offset suggests longer gap context
  if (hasNextMorning && (reg.lastDayOffset !== null && reg.lastDayOffset >= 2)) {
    const m = lowMarks.find(x => x.low.includes('next morning'))!;
    issues.push({
      type: 'timeline',
      severity: 'should-fix',
      description: 'Possible misalignment: "next morning" but earlier scenes imply a multi-day gap.',
      textSpan: [m.start, m.end],
    });
  }

  // Seasonal inconsistency
  const prevSeasons = new Set(Array.from(reg.seasons));
  const winterPrev = prevSeasons.has('winter');
  const summerPrev = prevSeasons.has('summer');
  const winterCur = curSeasons.has('winter');
  const summerCur = curSeasons.has('summer');

  if ((winterPrev && summerCur) || (summerPrev && winterCur)) {
    const m = lowMarks.find(x => seasonBucketSet([x]).size > 0) ?? lowMarks[0];
    issues.push({
      type: 'timeline',
      severity: 'should-fix',
      description: 'Seasonal inconsistency: prior scenes indicate different season without transition.',
      textSpan: [m.start, m.end],
    });
  } else if (curSeasons.size && prevSeasons.size && Array.from(curSeasons).some(s => !prevSeasons.has(s))) {
    const m = lowMarks.find(x => seasonBucketSet([x]).size > 0) ?? lowMarks[0];
    issues.push({
      type: 'timeline',
      severity: 'consider',
      description: 'Seasonal drift detected; consider adding transition context.',
      textSpan: [m.start, m.end],
    });
  }

  return issues;
}

// ---------- AI request helpers ----------
function buildReaderContextMinimal(): ReaderKnowledge {
  return {
    knownCharacters: new Set(),
    establishedTimeline: [],
    revealedPlotPoints: [],
    establishedSettings: [],
  };
}

function buildAIHeader(scene: Scene, targets: readonly TimelineDetectionTarget[], reg: PreviousTimelineRegistry): string {
  const lines: string[] = [];
  lines.push('[[Timeline detection targets]]');
  lines.push(`scene: id=${scene.id} pos=${scene.position}`);
  for (const c of targets.slice(0, 8)) {
    const sent = (c.sentenceText || '').slice(0, 140).replace(/\n+/g, ' ');
    const others = c.otherMarkers.slice(0, 6).join(' | ') || 'none';
    lines.push(`- marker="${c.markerText}" span=[${c.start},${c.end}] sent="${sent}" localMarkers=[${others}]`);
  }
  lines.push('[[Previous registry]]');
  lines.push(`lastAnchor=${reg.lastAnchor ?? 'none'} lastDayOffset=${reg.lastDayOffset ?? 'n/a'}`);
  lines.push(`seasons: ${Array.from(reg.seasons).slice(0, 8).join(' | ')}`);
  lines.push(`months: ${Array.from(reg.months).slice(0, 8).join(' | ')}`);
  lines.push('[[Scene excerpt]]');
  return lines.join('\n');
}

function buildSceneExcerptAroundTargets(text: string, targets: readonly TimelineDetectionTarget[], maxLen = 1200): string {
  if (!targets.length) return text.slice(0, maxLen);
  const parts: string[] = [];
  for (const c of targets) {
    parts.push(snippet(text, c.start, c.end, 220));
    const len = parts.join('\n---\n').length;
    if (len > maxLen) break;
  }
  return parts.join('\n---\n').slice(0, maxLen);
}

function mapAITimelineIssues(
  resp: { issues?: ContinuityIssue[] } | null | undefined,
  sceneText: string,
  targets: readonly TimelineDetectionTarget[]
): ContinuityIssue[] {
  const out: ContinuityIssue[] = [];
  const byMarker = new Map<string, TimelineDetectionTarget>();
  for (const c of targets) byMarker.set(c.markerText.toLowerCase(), c);
 
  for (const it of resp?.issues ?? []) {
    if ((it.type ?? 'timeline') !== 'timeline') continue;
    const hasSpan = Array.isArray(it.textSpan) && Number.isFinite(it.textSpan[0]) && Number.isFinite(it.textSpan[1]);
    if (hasSpan) {
      out.push({
        type: 'timeline',
        severity: it.severity ?? 'should-fix',
        description: it.description ?? 'Timeline consistency issue',
        textSpan: it.textSpan as [number, number],
        suggestedFix: it.suggestedFix,
      });
      continue;
    }
    // Fallback to closest detection target by marker mention
    const key = (it.description ?? '').toLowerCase();
    let chosen: TimelineDetectionTarget | undefined;
    for (const [mk, c] of byMarker) { if (key.includes(mk)) { chosen = c; break; } }
    chosen ??= targets[0];
    const span: [number, number] = chosen ? [chosen.start, chosen.end] : [0, Math.min(1, sceneText.length)];
    out.push({
      type: 'timeline',
      severity: it.severity ?? 'should-fix',
      description: it.description ?? `Timeline issue near "${chosen?.markerText ?? 'marker'}"`,
      textSpan: span,
      suggestedFix: it.suggestedFix,
    });
  }
  return out;
}

// ---------- Detector implementation ----------
export default class TimelineDetector extends BaseDetector<TimelineDetectionTarget> {
  public readonly detectorType = 'timeline' as const;

  protected async localDetection(
    scene: Scene,
    previousScenes: readonly Scene[],
    _aiManager: AIServiceManager
  ): Promise<LocalDetectionResult<TimelineDetectionTarget>> {
    if (!scene?.text || typeof scene.text !== 'string' || scene.text.trim().length === 0) {
      return { issues: [], requiresAI: false, targets: [] };
    }
 
    const doc = await this.safeNLP(scene.text);
    if (!doc) console.debug('[TimelineDetector] compromise not available, using regex-only fallback.');
    const sentences = splitSentences(scene.text);
    const extracted = extractTemporalMarkers(scene.text, doc);
    const basicMarkers = extracted.markers;
    const reg = getOrBuildRegistry(previousScenes);
 
    if (basicMarkers.length === 0 && reg.seasons.size === 0 && reg.months.size === 0 && reg.lastDayOffset === null) {
      console.debug('[TimelineDetector] Fast path: no markers and empty registry.');
      return { issues: [], requiresAI: false, targets: [] };
    }
 
    const issues = detectTimelineIssues(basicMarkers, reg, scene.text);
    const targets = assembleTimelineDetectionTargets(basicMarkers, scene.text, sentences, reg);
 
    console.debug(
      '[TimelineDetector] markers:',
      basicMarkers.length,
      'issues:',
      issues.length,
      'targets:',
      targets.length,
      'reg[lastAnchor,lastDayOffset,seasons,months]:',
      reg.lastAnchor,
      reg.lastDayOffset,
      Array.from(reg.seasons).join(','),
      Array.from(reg.months).join(',')
    );
 
    return {
      issues,
      requiresAI: targets.length > 0,
      targets,
      stats: {
        markers: basicMarkers.length,
        seasonsCurrent: seasonBucketSet(basicMarkers).size,
        hasMeanwhilePrev: reg.hasMeanwhile ? 1 : 0,
        targets: targets.length,
      },
    };
  }

  protected async aiDetection(
    scene: Scene,
    previousScenes: readonly Scene[],
    aiManager: AIServiceManager,
    targets: readonly TimelineDetectionTarget[]
  ): Promise<ContinuityIssue[]> {
    if (!targets || targets.length === 0) return [];
    try {
      const reg = getOrBuildRegistry(previousScenes);
      const header = buildAIHeader(scene, targets, reg);
      const excerpt = buildSceneExcerptAroundTargets(scene.text, targets, 1200);
      const prevExcerpt = previousScenes.length
        ? [{ ...previousScenes[previousScenes.length - 1], text: (previousScenes[previousScenes.length - 1].text ?? '').slice(0, 700) }]
        : [];

      const baseReq = {
        scene: { ...scene, text: `${header}\n\n${excerpt}` },
        previousScenes: prevExcerpt as Scene[],
        analysisType: 'consistency' as const,
        readerContext: buildReaderContextMinimal(),
      } as Parameters<AIServiceManager['analyzeContinuity']>[0];

      const enriched = enrichAnalysisRequest(baseReq as any, {
        scene,
        detectorType: 'timeline',
        flags: { critical: Boolean((scene as any)?.critical) },
      });

      console.debug('[TimelineDetector] invoking AI (consistency) for targets:', targets.length);
      const { issues } = await runAnalysisWithOptionalConsensus(aiManager, enriched as any, {
        critical: Boolean((enriched as any)?.flags?.critical),
        consensusCount: 2,
        acceptThreshold: 0.5,
        humanReviewThreshold: 0.9,
        maxModels: 2,
      });

      const out = mapAITimelineIssues({ issues }, scene.text, targets);
      console.debug('[TimelineDetector] AI returned timeline issues:', out.length);
      return out;
    } catch (err) {
      console.debug('[TimelineDetector] AI analyzeContinuity failed; degrading to local-only.', err);
      return [];
    }
  }
}

import { describe, it, beforeEach, expect, vi } from 'vitest';
import useRewriteStore from '../stores/rewriteStore';
import { useManuscriptStore } from '../../../stores/manuscriptStore';
import type { Manuscript, Scene, ContinuityIssue, ReaderKnowledge } from '../../../../shared/types';
import type { BatchRewriteProgress, BatchRewriteOptions } from '../../../../services/rewrite/RewriteOrchestrator';
import type { RewriteResult } from '../../../../services/rewrite/SceneRewriter';

function buildIssue(): ContinuityIssue {
  return {
    type: 'pronoun',
    severity: 'must-fix',
    description: 'Ambiguous pronoun reference',
    textSpan: [0, 5],
    suggestedFix: 'Clarify subject'
  };
}

function buildScene(id: string, position: number): Scene {
  const readerContext: ReaderKnowledge = {
    knownCharacters: new Set(),
    establishedTimeline: [],
    revealedPlotPoints: [],
    establishedSettings: []
  };

  return {
    id,
    text: `Original ${id}`,
    wordCount: 2,
    position,
    originalPosition: position,
    characters: [],
    timeMarkers: [],
    locationMarkers: [],
    hasBeenMoved: true,
    rewriteStatus: 'pending',
    continuityAnalysis: {
      issues: [buildIssue()],
      timestamp: Date.now(),
      modelUsed: 'test',
      confidence: 0.9,
      readerContext
    }
  };
}

function buildManuscript(ids: string[]): Manuscript {
  const scenes = ids.map((id, idx) => buildScene(id, idx));
  return {
    id: 'm-batch',
    title: 'Batch Manuscript',
    scenes,
    originalOrder: scenes.map(s => s.id),
    currentOrder: scenes.map(s => s.id)
  };
}

class OrchestratorFake {
  cancelled = false;

  cancelBatch() {
    this.cancelled = true;
  }

  async rewriteMovedScenes(
    manuscript: Pick<Manuscript, 'scenes' | 'currentOrder'>,
    options: BatchRewriteOptions = {}
  ): Promise<BatchRewriteProgress> {
    const sceneIds = manuscript.currentOrder;

    const progress: BatchRewriteProgress = {
      totalScenes: sceneIds.length,
      completedScenes: 0,
      phase: 'preparing',
      message: 'Preparing batch rewrite...',
      results: new Map(),
      errors: new Map()
    };

    options.progressCallback?.(progress);

    // Simulate sequential processing
    for (const id of sceneIds) {
      if (this.cancelled) {
        progress.phase = 'error';
        progress.message = 'Batch rewrite cancelled';
        break;
      }

      progress.currentSceneId = id;
      progress.currentSceneTitle = manuscript.scenes.find(s => s.id === id)?.text.split('\n')[0];
      progress.phase = 'rewriting';
      progress.message = `Rewriting scene ${progress.completedScenes + 1} of ${progress.totalScenes}`;
      options.progressCallback?.(progress);

      // Simulate result
      const result: RewriteResult = {
        success: true,
        rewrittenText: `Rewritten ${id}`,
        issuesAddressed: manuscript.scenes.find(s => s.id === id)?.continuityAnalysis?.issues ?? [],
        changesExplanation: `Fixed issues in ${id}`,
        preservedElements: [],
        diffData: [],
        modelUsed: 'fake-model'
      };
      progress.results.set(id, result);
      progress.completedScenes += 1;
      options.progressCallback?.(progress);
    }

    if (!this.cancelled) {
      progress.phase = 'complete';
      progress.message = `Successfully rewrote ${progress.results.size} scenes`;
    }

    return progress;
  }
}

describe('Rewrite Store ‚Äî batch operations', () => {
