import type { Scene, ContinuityIssue, ReaderKnowledge } from '../../../shared/types';
import type { AnalysisRequest, AnalysisType } from '../types';
import AIServiceManager from '../AIServiceManager';
import { ValidationPipeline } from '../validation/ValidationPipeline';

// Local mapping helpers (keep tiny and self-contained)
function mapContinuityTypeToSchema(t: ContinuityIssue['type']): 'pronoun_reference' | 'timeline' | 'character_knowledge' | 'other' {
  switch (t) {
    case 'pronoun': return 'pronoun_reference';
    case 'timeline': return 'timeline';
    case 'character': return 'character_knowledge';
    default: return 'other';
  }
}

function mapSchemaTypeToContinuity(t: any): ContinuityIssue['type'] {
  const s = String(t ?? '').toLowerCase();
  if (s === 'pronoun_reference') return 'pronoun';
  if (s === 'timeline') return 'timeline';
  if (s === 'character_knowledge') return 'character';
  // Prefer plot/context over engagement for generic "other"
  return 'plot';
}

function mapContinuitySeverityToSchema(s: ContinuityIssue['severity']): 'low' | 'medium' | 'high' | 'critical' {
  switch (s) {
    case 'must-fix': return 'high';
    case 'should-fix': return 'medium';
    case 'consider': return 'low';
  }
}

function mapSchemaSeverityToContinuity(s: any): ContinuityIssue['severity'] {
  const v = String(s ?? '').toLowerCase();
  if (v === 'critical' || v === 'high') return 'must-fix';
  if (v === 'medium') return 'should-fix';
  return 'consider';
}

function toPipelineIssues(issues: readonly ContinuityIssue[]) {
  return (issues ?? []).map((it) => ({
    type: mapContinuityTypeToSchema(it.type),
    severity: mapContinuitySeverityToSchema(it.severity),
    span: Array.isArray(it.textSpan) && it.textSpan.length === 2
      ? { start_index: it.textSpan[0], end_index: it.textSpan[1] }
      : null,
    explanation: String(it.description ?? '').trim() || 'Issue detected',
    evidence: [],
    suggested_fix: String(it.suggestedFix ?? ''),
    confidence: undefined as number | undefined,
  }));
}

function fromPipelineIssues(pIssues: any[]): ContinuityIssue[] {
  const out: ContinuityIssue[] = [];
  for (const it of pIssues ?? []) {
    const start = it?.span?.start_index;
    const end = it?.span?.end_index;
    const hasSpan = Number.isFinite(start) && Number.isFinite(end) && (end as number) >= (start as number);
    out.push({
      type: mapSchemaTypeToContinuity(it?.type),
      severity: mapSchemaSeverityToContinuity(it?.severity),
      description: String(it?.explanation ?? it?.suggested_fix ?? 'Issue detected'),
      textSpan: hasSpan ? [start, end] as [number, number] : [0, 1],
      suggestedFix: it?.suggested_fix ? String(it.suggested_fix) : undefined,
    });
  }
  return out;
}

// Default small candidate set (local-only; do not import provider registries)
const DEFAULT_CANDIDATES = ['gpt-5', 'claude-sonnet-4'];

export type ExtraFlags = { complex?: boolean; critical?: boolean };

export function enrichAnalysisRequest(
  req: AnalysisRequest & Record<string, any>,
  extras: {
    scene: Scene;
    detectorType: string; // stable taskType label (pronoun, timeline, character, plot, engagement, continuity_rewrite, etc.)
    flags?: ExtraFlags;
  }
): AnalysisRequest & Record<string, any> {
  const newPosition =
    typeof (extras.scene as any)?.position === 'number'
      ? (extras.scene as any).position
      : typeof (extras.scene as any)?.index === 'number'
      ? (extras.scene as any).index
      : 0;

  return Object.assign(req as any, {
    // Stable routing/task labels (kept local; manager tolerates extra props)
    taskType: extras.detectorType,
    detector: extras.detectorType,
    // Flags for heuristics/consensus selection (local only)
    flags: { ...(req as any)?.flags, ...(extras.flags ?? {}) },
    // Local meta (never part of PromptCache identity)
    sceneText: String(extras.scene?.text ?? ''),
    newPosition,
  });
}

/**
 * Run continuity analysis with optional consensus for critical/high-stakes cases.
 * - Preserves PromptCache identity by leaving key fields unchanged.
 * - Injects __modelId into the req for each run so tests can assert distinct candidates.
 * - Returns an object with { issues } compatible with existing mapping utilities.
 */
export async function runAnalysisWithOptionalConsensus(
  aiManager: AIServiceManager,
  req: AnalysisRequest & Record<string, any>,
  opts: {
    critical?: boolean;
    candidates?: string[];
    consensusCount?: number;
    acceptThreshold?: number;
    humanReviewThreshold?: number;
    maxModels?: number;
  } = {}
): Promise<{ issues: ContinuityIssue[] }> {
  const critical = !!opts.critical;
  if (!critical) {
    const resp = await aiManager.analyzeContinuity(req);
    return { issues: resp.issues ?? [] };
  }

  const candidates = (opts.candidates && opts.candidates.length > 0) ? Array.from(new Set(opts.candidates)) : DEFAULT_CANDIDATES.slice(0, 2);

  const pipeline = new ValidationPipeline(
    async (r, { modelId } = {}) => {
      // Attach model id onto the request (local-only, tolerated by manager)
      const r2 = { ...(r as any), __modelId: modelId };
      const single = await aiManager.analyzeContinuity(r2 as any);
      // Convert manager result issues (ContinuityIssue[]) to pipeline schema
      const data = {
        issues: toPipelineIssues(single.issues ?? []),
        summary: '',
        confidence: typeof single?.metadata?.confidence === 'number' ? single.metadata.confidence : 0,
      };
      return { data, modelId, latencyMs: typeof single?.metadata?.durationMs === 'number' ? single.metadata.durationMs : undefined };
    },
    {
      defaultConsensusCount: opts.consensusCount ?? 2,
      acceptThreshold: typeof opts.acceptThreshold === 'number' ? opts.acceptThreshold : 0.5,
      humanReviewThreshold: typeof opts.humanReviewThreshold === 'number' ? opts.humanReviewThreshold : 0.9,
      maxModels: typeof opts.maxModels === 'number' ? opts.maxModels : 2,
    }
  );

  const { data } = await pipeline.runConsensus(req, {
    candidates,
    consensusCount: opts.consensusCount ?? 2,
    acceptThreshold: typeof opts.acceptThreshold === 'number' ? opts.acceptThreshold : 0.5,
    humanReviewThreshold: typeof opts.humanReviewThreshold === 'number' ? opts.humanReviewThreshold : 0.9,
    maxModels: typeof opts.maxModels === 'number' ? opts.maxModels : 2,
  });

  return { issues: fromPipelineIssues((data as any)?.issues ?? []) };
}

/**
 * Specialized minimal adapter for rewrite flows:
 * - For consensus: runs two model attempts and treats the returned "rewrite text" as the pipeline summary.
 * - After consensus, uses merged summary as the chosen rewrite text.
 * - For single run: returns the original manager result.
 */
export async function runRewriteWithOptionalConsensus(
  aiManager: AIServiceManager,
  req: AnalysisRequest & Record<string, any>,
  opts: {
    critical?: boolean;
    candidates?: string[];
    consensusCount?: number;
    acceptThreshold?: number;
    humanReviewThreshold?: number;
    maxModels?: number;
  } = {}
): Promise<any> {
  const critical = !!opts.critical;
  if (!critical) {
    return aiManager.analyzeContinuity(req);
  }

  const candidates = (opts.candidates && opts.candidates.length > 0) ? Array.from(new Set(opts.candidates)) : DEFAULT_CANDIDATES.slice(0, 2);

  const pipeline = new ValidationPipeline(
    async (r, { modelId } = {}) => {
      const r2 = { ...(r as any), __modelId: modelId };
      const single = await aiManager.analyzeContinuity(r2 as any);
      // Extract rewrite text from tolerant locations
      const rewriteText =
        (single as any)?.rewrittenText ??
        (single as any)?.content?.[0]?.text ??
        (single as any)?.text ??
        '';
      const data = {
        issues: [],      // rewrite consensus reconciles on "summary" only
        summary: String(rewriteText ?? ''),
        confidence: typeof (single as any)?.metadata?.confidence === 'number' ? (single as any).metadata.confidence : 0.5,
      };
      return { data, modelId };
    },
    {
      defaultConsensusCount: opts.consensusCount ?? 2,
      acceptThreshold: typeof opts.acceptThreshold === 'number' ? opts.acceptThreshold : 0.5,
      humanReviewThreshold: typeof opts.humanReviewThreshold === 'number' ? opts.humanReviewThreshold : 0.9,
      maxModels: typeof opts.maxModels === 'number' ? opts.maxModels : 2,
    }
  );

  const { data } = await pipeline.runConsensus(req, {
    candidates,
    consensusCount: opts.consensusCount ?? 2,
    acceptThreshold: typeof opts.acceptThreshold === 'number' ? opts.acceptThreshold : 0.5,
    humanReviewThreshold: typeof opts.humanReviewThreshold === 'number' ? opts.humanReviewThreshold : 0.9,
    maxModels: typeof opts.maxModels === 'number' ? opts.maxModels : 2,
  });

  // Return a shape consumable by SceneRewriter.parseRewriteResponse
  return { text: String((data as any)?.summary ?? '') };
}

import BaseProvider from './BaseProvider';
import {
  AnalysisRequest,
  AnalysisResponse,
  ClaudeConfig,
  ProviderError,
  ProviderName,
  ValidationError,
} from '../types';
import { validateAndNormalize } from '../utils/ResponseValidator';
import { buildClaudePrompt } from '../prompts/ClaudePrompts';
import { estimateMessageTokens, estimateTokensForModel } from '../utils/Tokenizers';
import { estimateCost as estimateUsdCost } from '../optimization/Pricing';

const ANTHROPIC_VERSION = '2023-06-01';

function costTierForModel(model: string | undefined): 'low' | 'medium' | 'high' {
  const m = (model ?? '').toLowerCase();
  if (m.includes('opus') || m.includes('o')) return 'high';
  if (m.includes('sonnet') || m.includes('haiku') || m.includes('sonnet-4')) return 'medium';
  return 'medium';
}

/**
 * Claude (Anthropic) provider implementation using Messages API.
 */
export class ClaudeProvider extends BaseProvider<ClaudeConfig> {
  constructor(config: ClaudeConfig, breaker: import('../utils/CircuitBreaker').default) {
    super('anthropic' as ProviderName, config, breaker);
  }

  /**
   * Build Anthropic-specific prompt payload using XML-style template.
   */
  protected formatPrompt(req: AnalysisRequest): string {
    const readerContext = req.readerContext;
    const previousScenes = req.previousScenes;
    const newPosition = req.scene.position;
    const sceneText = req.scene.text;
    return buildClaudePrompt(readerContext, previousScenes, newPosition, sceneText);
  }

  /**
   * Execute continuity analysis via Anthropic Messages API.
   */
  public async analyze(req: AnalysisRequest): Promise<AnalysisResponse> {
    if (!this.config.apiKey) {
      throw new ProviderError('anthropic', 'Missing Anthropic API key in configuration');
    }

    const modelLabel = this.config.model ?? 'claude-sonnet-4';
    const url =
      this.config.baseUrl?.trim() ||
      'https://api.anthropic.com/v1/messages';

    // Enforce optional budgets by trimming older previousScenes
    const budgeted = this.enforceInputBudget(req, modelLabel);
    const effReq = budgeted.req;

    const prompt = this.formatPrompt(effReq) as string;

    const body = {
      model: modelLabel,
      system:
        'You are a meticulous continuity analyst. Respond with ONLY valid JSON per the requested schema. No extra text.',
      messages: [
        {
          role: 'user',
          content: [{ type: 'text', text: prompt }],
        },
      ],
      max_tokens: 2048,
      temperature: 0.2,
    };

    // Pre-flight estimate of input tokens using simplified message view
    let inputTokensEstimate = estimateMessageTokens(modelLabel, [
      { role: 'system', content: body.system },
      { role: 'user', content: prompt },
    ]);

    const headers: HeadersInit = {
      'content-type': 'application/json',
      'x-api-key': this.config.apiKey,
      'anthropic-version': ANTHROPIC_VERSION,
    };

    const started = Date.now();
    try {
      const res = await this.fetchWithRetry(url, {
        method: 'POST',
        headers,
        body: JSON.stringify(body),
      });

      const raw = (await res.json()) as any;
      const normalized = validateAndNormalize('anthropic', raw, modelLabel);

      // Prefer provider usage when available
      if (raw && raw.usage && Number.isFinite(raw.usage.input_tokens)) {
        inputTokensEstimate = Math.max(0, Number(raw.usage.input_tokens));
      }

      // Output tokens: prefer usage, else estimate from first content text
      let outputText = '';
      try {
        outputText = raw?.content?.[0]?.text ?? '';
      } catch {}
      let outputTokensEstimate =
        raw && raw.usage && Number.isFinite(raw.usage.output_tokens)
          ? Math.max(0, Number(raw.usage.output_tokens))
          : estimateTokensForModel(modelLabel, String(outputText ?? ''));

      const durationMs = Date.now() - started;

      const costEstimate = this.estimateCostFromUsage(modelLabel, {
        inputTokens: inputTokensEstimate,
        outputTokens: outputTokensEstimate,
      });

      const breakdown = estimateUsdCost(modelLabel, {
        inputTokens: inputTokensEstimate,
        outputTokens: outputTokensEstimate,
      }).breakdown;

      const confidence = normalized.metadata.confidence ?? 0.5;

      const meta: any = {
        modelUsed: modelLabel,
        provider: 'anthropic',
        costEstimate,
        durationMs,
        confidence,
        cached: false,
      };
      if (budgeted.meta) {
        meta.trimmed = true;
        meta.trimDetails = budgeted.meta;
      }
      meta.tokensInputEstimated = inputTokensEstimate;
      meta.tokensOutputEstimated = outputTokensEstimate;
      meta.costBreakdownUSD = breakdown;

      const out: AnalysisResponse = {
        issues: normalized.issues,
        metadata: meta as AnalysisResponse['metadata'],
      };

      return out;
    } catch (err) {
      // TODO: Replace with production logger
      console.log('[ClaudeProvider] analyze error:', err);
      if (err instanceof ValidationError) {
        throw err;
      }
      if (err && typeof err === 'object' && (err as Error).name === 'CircuitBreakerOpenError') {
        throw err;
      }
      throw new ProviderError('anthropic', 'Claude analyze failed', { cause: err });
    }
  }
}

export default ClaudeProvider;

import BaseProvider from './BaseProvider';
import {
  AnalysisRequest,
  AnalysisResponse,
  GeminiConfig,
  ProviderError,
  ProviderName,
  ValidationError,
} from '../types';
import { validateAndNormalize } from '../utils/ResponseValidator';
import { buildGeminiPrompt } from '../prompts/GeminiPrompts';
import { estimateMessageTokens, estimateTokensForModel } from '../utils/Tokenizers';
import { estimateCost as estimateUsdCost } from '../optimization/Pricing';

function buildGeminiUrl(model: string, apiKey: string, baseUrl?: string): string {
  if (baseUrl && baseUrl.trim().length > 0) {
    // Expect caller to include model + api key if they override baseUrl
    return baseUrl;
  }
  const encodedModel = encodeURIComponent(model);
  return `https://generativelanguage.googleapis.com/v1beta/models/${encodedModel}:generateContent?key=${encodeURIComponent(
    apiKey
  )}`;
}

function costTierForModel(model: string | undefined): 'low' | 'medium' | 'high' {
  const m = (model ?? '').toLowerCase();
  if (m.includes('pro')) return 'low';
  if (m.includes('flash') || m.includes('lite')) return 'low';
  return 'medium';
}

/**
 * Google Gemini provider implementation using generateContent API.
 */
export class GeminiProvider extends BaseProvider<GeminiConfig> {
  constructor(config: GeminiConfig, breaker: import('../utils/CircuitBreaker').default) {
    super('google' as ProviderName, config, breaker);
  }

  /**
   * Build Gemini-specific content structure with instruction and parts.
   */
  protected formatPrompt(req: AnalysisRequest): { instruction: string; parts: Array<{ text: string }> } {
    const readerContext = req.readerContext;
    const previousScenes = req.previousScenes;
    const newPosition = req.scene.position;
    const sceneText = req.scene.text;
    const genreStyle = (req as any).genreStyle ?? (req.scene as any)?.genreStyle ?? undefined;

    return buildGeminiPrompt({
      readerContext,
      previousScenes,
      newPosition,
      sceneText,
      genreStyle,
    });
  }

  /**
   * Execute continuity analysis via Gemini generateContent.
   */
  public async analyze(req: AnalysisRequest): Promise<AnalysisResponse> {
    if (!this.config.apiKey) {
      throw new ProviderError('google', 'Missing Google API key in configuration');
    }

    const modelLabel = this.config.model ?? 'gemini-2-5-pro';
    const url = buildGeminiUrl(modelLabel, this.config.apiKey, this.config.baseUrl);

    // Enforce optional input budgets (trim oldest previousScenes)
    const budgeted = this.enforceInputBudget(req, modelLabel);
    const effReq = budgeted.req;

    const { instruction, parts } = this.formatPrompt(effReq) as { instruction: string; parts: Array<{ text: string }> };

    const body = {
      systemInstruction: {
        role: 'system',
        parts: [{ text: instruction }],
      },
      contents: [
        {
          role: 'user',
          parts,
        },
      ],
      generationConfig: {
        temperature: 0.2,
        // Prefer JSON-only responses if supported by the API
        response_mime_type: 'application/json',
      },
    };

    // Pre-flight estimate of input tokens using simplified message view
    let inputTokensEstimate = estimateMessageTokens(modelLabel, [
      { role: 'system', content: instruction },
      { role: 'user', content: parts.map((p) => p.text ?? '').join('\n') },
    ]);

    const headers: HeadersInit = {
      'content-type': 'application/json',
    };

    const started = Date.now();
    try {
      const res = await this.fetchWithRetry(url, {
        method: 'POST',
        headers,
        body: JSON.stringify(body),
      });

      const raw = (await res.json()) as any;
      const normalized = validateAndNormalize('google', raw, modelLabel);

      // Gemini v1beta responses may not include token usage in public API.
      // If present in future, prefer it; else heuristic on text length.
      // Attempt to extract output text
      let outputText = '';
      try {
        const firstCandidate = raw?.candidates?.[0];
        const partsOut = firstCandidate?.content?.parts ?? [];
        const withText = partsOut.find((p: { text?: string }) => typeof p.text === 'string' && ((p.text?.length ?? 0) > 0));
        outputText = withText?.text ?? '';
      } catch {}

      // Token usage fields if they appear in future:
      const usageIn = Number(raw?.usageMetadata?.promptTokenCount);
      const usageOut = Number(raw?.usageMetadata?.candidatesTokenCount);
      if (Number.isFinite(usageIn)) inputTokensEstimate = Math.max(0, usageIn);

      const outputTokensEstimate = Number.isFinite(usageOut)
        ? Math.max(0, usageOut)
        : estimateTokensForModel(modelLabel, String(outputText ?? ''));

      const durationMs = Date.now() - started;

      const costEstimate = this.estimateCostFromUsage(modelLabel, {
        inputTokens: inputTokensEstimate,
        outputTokens: outputTokensEstimate,
      });

      const breakdown = estimateUsdCost(modelLabel, {
        inputTokens: inputTokensEstimate,
        outputTokens: outputTokensEstimate,
      }).breakdown;

      const confidence = normalized.metadata.confidence ?? 0.5;

      const meta: any = {
        modelUsed: modelLabel,
        provider: 'google',
        costEstimate,
        durationMs,
        confidence,
        cached: false,
      };
      if (budgeted.meta) {
        meta.trimmed = true;
        meta.trimDetails = budgeted.meta;
      }
      meta.tokensInputEstimated = inputTokensEstimate;
      meta.tokensOutputEstimated = outputTokensEstimate;
      meta.costBreakdownUSD = breakdown;

      const out: AnalysisResponse = {
        issues: normalized.issues,
        metadata: meta as AnalysisResponse['metadata'],
      };

      return out;
    } catch (err) {
      // TODO: Replace with production logger
      console.log('[GeminiProvider] analyze error:', err);
      if (err instanceof ValidationError) {
        throw err;
      }
      if (err && typeof err === 'object' && (err as Error).name === 'CircuitBreakerOpenError') {
        throw err;
      }
      throw new ProviderError('google', 'Gemini analyze failed', { cause: err });
    }
  }
}

