}

/**
 * Legacy normalized response "schema" for ContinuityIssue[] (kept for compatibility).
 * Public export maintained to avoid breaking external imports.
 */
export function normalizedResponseSchema(): Parser<{
  issues: ContinuityIssue[];
  metadata?:
    | {
        modelUsed?: string;
        provider?: ProviderName;
        costEstimate?: number;
        durationMs?: number;
        confidence?: number;
        cached?: boolean;
      }
    | undefined;
}> {
  return {
    parse(
      raw: unknown
    ): {
      issues: ContinuityIssue[];
      metadata?:
        | {
            modelUsed?: string;
            provider?: ProviderName;
            costEstimate?: number;
            durationMs?: number;
            confidence?: number;
            cached?: boolean;
          }
        | undefined;
    } {
      if (!isObject(raw)) {
        throw new Error('Invalid normalized response: expected an object');
      }

      const issuesRaw = (raw as Record<string, unknown>).issues;
      let issues: ContinuityIssue[] = [];
      if (issuesRaw === undefined) {
        issues = [];
      } else {
        if (!Array.isArray(issuesRaw)) {
          throw new Error("Invalid normalized response: 'issues' must be an array");
        }
        issuesRaw.forEach((item, idx) => {
          if (!isContinuityIssue(item)) {
            // Provide a precise messaging on failure
            throw new Error(`Invalid ContinuityIssue at index ${idx}`);
          }
        });
        issues = issuesRaw as ContinuityIssue[];
      }

      const metadata = validateNormalizedMetadata((raw as Record<string, unknown>).metadata);

      return { issues, metadata };
    },
  };
}

// ------------ Utilities: JSON extraction and sanitization ------------

/**
 * Extract the first JSON object from arbitrary text using bracket counting.
 * Handles nested braces and ignores braces inside strings.
 */
export function extractJsonFromText(text: string): string {
  // Strip code fences if present
  const fenceMatch = text.match(/```(?:json)?\s*([\s\S]*?)```/i);
  if (fenceMatch) {
    text = fenceMatch[1];
  }

  let start = -1;
  let depth = 0;
  let inString: false | '"' | "'" = false;
  let escape = false;

  for (let i = 0; i < text.length; i++) {
    const ch = text[i];

    if (inString) {
      if (escape) {
        escape = false;
      } else if (ch === '\\') {
        escape = true;
      } else if (ch === inString) {
        inString = false;
      }
      continue;
    }

    if (ch === '"' || ch === "'") {
      inString = ch as '"' | "'";
      continue;
    }

    if (ch === '{') {
      if (depth === 0) start = i;
      depth++;
    } else if (ch === '}') {
      depth--;
      if (depth === 0 && start !== -1) {
        return text.slice(start, i + 1);
      }
    }
  }
  // Fallback: trim and try if entire string looks like JSON
  const trimmed = text.trim();
  if (trimmed.startsWith('{') && trimmed.endsWith('}')) return trimmed;
  throw new Error('No JSON object found in text');
}

function stripBOM(s: string): string {
  return s.charCodeAt(0) === 0xfeff ? s.slice(1) : s;
}
function replaceSmartQuotes(s: string): string {
  return s
    .replace(/[\u2018\u2019\u201A\u201B]/g, "'")
    .replace(/[\u201C\u201D\u201E\u201F]/g, '"');
}
function removeComments(s: string): string {
  // Remove //... and /* ... */
  return s.replace(/\/\/[^\n\r]*|\/\*[\s\S]*?\*\//g, '');
}
function removeTrailingCommas(s: string): string {
  // Remove trailing commas before } or ]
  return s.replace(/,(\s*[}\]])/g, '$1');
}
function fixSingleQuotedKeysAndStrings(s: string): string {
  // 'key': value  -> "key": value
  s = s.replace(/([{,\s])'([A-Za-z0-9_]+)'\s*:/g, '$1"$2":');
  // :"value'like" -> conservative conversion for single-quoted string values
  s = s.replace(/:\s*'([^'\\]*(?:\\.[^'\\]*)*)'/g, ': "$1"');
  return s;
}
function quoteUnquotedKeys(s: string): string {
  // { key: ... , another_key: ... } -> quote keys
  return s.replace(/([{,\s])([A-Za-z_][A-Za-z0-9_]*)\s*:/g, '$1"$2":');
}
function sanitizeJsonLike(s: string, { quoteKeys = false }: { quoteKeys?: boolean } = {}): string {
  let out = stripBOM(s);
  out = replaceSmartQuotes(out);
  out = removeComments(out);
  out = removeTrailingCommas(out);
  out = fixSingleQuotedKeysAndStrings(out);
  if (quoteKeys) {
    out = quoteUnquotedKeys(out);
  }
  return out;
}

/**
 * Fallback slice for the outermost JSON object when strict extraction fails.
 * Returns substring between the first '{' and the last '}' if present.
 */
function sliceOuterJson(s: string): string | null {
  const start = s.indexOf('{');
  const end = s.lastIndexOf('}');
  if (start !== -1 && end !== -1 && end > start) {
    return s.slice(start, end + 1);
  }
  return null;
}

// ------------ Model response normalization and confidence scoring ------------

export type ValidationMeta = { attempts: number; repaired: boolean; errors: string[] };

function cleanText(s: string): string {
  return s.replace(/[\x00-\x1F\x7F]/g, '').trim();
}

function clamp01(x: number): number {
  if (!Number.isFinite(x)) return 0;
  return Math.max(0, Math.min(1, x));
}

function normalizeModelData(parsed: ModelAnalysisResponse): ModelAnalysisResponse {
  // Clone to avoid mutation of caller data
  const data: ModelAnalysisResponse = {
    issues: Array.isArray(parsed.issues) ? [...parsed.issues] : [],
    summary: typeof parsed.summary === 'string' ? parsed.summary : '',
    confidence: typeof parsed.confidence === 'number' ? clamp01(parsed.confidence) : undefined,
  };

  data.summary = cleanText(data.summary);

  const severityValues = SeverityEnum.options as readonly z.infer<typeof SeverityEnum>[];
  const typeValues = IssueTypeEnum.options as readonly z.infer<typeof IssueTypeEnum>[];

  data.issues = data.issues.map((issue: ModelAnalysisResponse['issues'][number]) => {
    let severity = issue.severity;
    // Case-insensitive severity coercion if needed (best-effort; schema enforces valid already)
    const sevLower = String(severity).toLowerCase();
    const sevCoerced = severityValues.find((v: z.infer<typeof SeverityEnum>) => v.toLowerCase() === sevLower) ?? severity;
    severity = sevCoerced as typeof issue.severity;

    let type = issue.type;
    const typeLower = String(type).toLowerCase();
    const typeCoerced =
      typeValues.find((v: z.infer<typeof IssueTypeEnum>) => v.toLowerCase() === typeLower) ?? type;
    type = typeCoerced as typeof issue.type;

    // Span normalization
    let span = issue.span ?? null;
    if (span) {
      const start = Math.max(0, Math.min(Number.MAX_SAFE_INTEGER, Math.trunc(span.start_index)));
      const end = Math.max(0, Math.min(Number.MAX_SAFE_INTEGER, Math.trunc(span.end_index)));
      span = {
        start_index: Math.max(0, Math.min(start, end)),
        end_index: Math.max(start, end),
      };
    }

    // Strings cleanup
    const explanation = cleanText(issue.explanation ?? '');
    const suggested_fix = cleanText(issue.suggested_fix ?? '');

    // Evidence cleanup, dedupe and cap
    const seen = new Set<string>();
    const evidence =
      Array.isArray(issue.evidence) ? issue.evidence.map((e: string) => cleanText(e)).filter((e: string) => e.length > 0) : [];
    const deduped: string[] = [];
    for (const e of evidence) {
      const key = e.toLowerCase();
      if (!seen.has(key)) {
        seen.add(key);
        deduped.push(e);
      }
      if (deduped.length >= 10) break;
    }

    // Confidence clamp if present
    let conf = issue.confidence;
    if (typeof conf === 'number') {
      conf = clamp01(conf);
    }

    return {
      type,
      severity,
      span: span ?? null,
      explanation,
      evidence: deduped,
      suggested_fix,
      confidence: conf,
    };
  });

  // Backfill issue confidences heuristically if missing
  const sevWeights: Record<z.infer<typeof SeverityEnum>, number> = {
    low: 0.4,
    medium: 0.6,
    high: 0.8,
    critical: 0.9,
  };

  data.issues = data.issues.map((issue: ModelAnalysisResponse['issues'][number]) => {
    if (typeof issue.confidence === 'number') return issue;
    let score = sevWeights[issue.severity] ?? 0.5;
    const bonus = Math.min(0.1, (issue.evidence?.length ?? 0) * 0.02);
    score += bonus;
    if (issue.span && Number.isFinite(issue.span.start_index) && Number.isFinite(issue.span.end_index)) {
      score += 0.05;
    }
    // Clamp into [0.35, 0.98]
    score = Math.max(0.35, Math.min(0.98, score));
    return { ...issue, confidence: clamp01(score) };
  });

  if (data.confidence === undefined) {
    const confidences = data.issues.map((i: ModelAnalysisResponse['issues'][number]) => i.confidence ?? 0);
    const mean = confidences.length ? confidences.reduce((a: number, b: number) => a + b, 0) / confidences.length : 0;
    data.confidence = clamp01(mean);
  }

  return data;
}

// ------------ Parsing pipeline with retries and fallbacks ------------

type ParseAttemptResult =
  | { ok: true; data: ModelAnalysisResponse; meta: ValidationMeta }
  | { ok: false; meta: ValidationMeta };

function tryZodValidate(candidate: unknown): { ok: true; data: ModelAnalysisResponse } | { ok: false; error: string } {
  const res = AnalysisResponseZodSchema.safeParse(candidate);
  if (res.success) return { ok: true, data: res.data };
  return { ok: false, error: res.error.errors.map((e: z.ZodIssue) => e.message).join('; ') };
}

function parseModelOutputToZod(raw: unknown, retries = 3): ParseAttemptResult {
  const errors: string[] = [];
  let attempts = 0;
  let repaired = false;
  let extractedSub: string | null = null;

  // 0) Already-object case
  if (isObject(raw)) {
    attempts++;
    const z = tryZodValidate(raw);
    if (z.ok) {
      return { ok: true, data: normalizeModelData(z.data), meta: { attempts, repaired, errors } };
    } else {
      errors.push(`Zod validation failed on object: ${z.error}`);
      // Continue to stringify-sanitize attempts below
    }
  }

  const rawStr = isString(raw) ? raw : (() => {
    try {
      return JSON.stringify(raw);
    } catch {
      return '';
    }
  })();

  // Strategy 1: Strict JSON.parse on full string
  attempts++;
  try {
    const candidate = JSON.parse(stripBOM(rawStr));
    const z = tryZodValidate(candidate);
    if (z.ok) {
      return { ok: true, data: normalizeModelData(z.data), meta: { attempts, repaired, errors } };
    }
    errors.push(`Zod validation failed on strict JSON: ${z.error}`);
  } catch (e) {
    errors.push(`Strict JSON.parse failed: ${(e as Error)?.message ?? String(e)}`);
  }

  // Strategy 2: Extract first top-level JSON object substring (handles fences too)
  attempts++;
  try {
    const jsonSub = extractJsonFromText(rawStr);
    extractedSub = jsonSub;
    if (jsonSub !== rawStr) repaired = true;
    const candidate = JSON.parse(jsonSub);
    const z = tryZodValidate(candidate);
    if (z.ok) {
      return { ok: true, data: normalizeModelData(z.data), meta: { attempts, repaired, errors } };
    }
    errors.push(`Zod validation failed on extracted JSON: ${z.error}`);
  } catch (e) {
    // Keep extractedSub as whatever was found (if any), but note failure
    repaired = true;
    errors.push(`Extraction/parse failed: ${(e as Error)?.message ?? String(e)}`);
  }

  // Strategy 3: Sanitize common issues (smart quotes, single quotes, trailing commas, comments)
  attempts++;
  try {
    const base = extractedSub ?? sliceOuterJson(rawStr) ?? rawStr;
    const sanitized = sanitizeJsonLike(base);
    if (sanitized !== base) repaired = true;
    const candidate = JSON.parse(sanitized);
    const z = tryZodValidate(candidate);
    if (z.ok) {
      return { ok: true, data: normalizeModelData(z.data), meta: { attempts, repaired, errors } };
    }
    errors.push(`Zod validation failed after sanitize: ${z.error}`);
  } catch (e) {
    repaired = true;
    errors.push(`Sanitize/parse failed: ${(e as Error)?.message ?? String(e)}`);
  }

  // Strategy 3b: Quote unquoted keys conservatively
  attempts++;
  try {
    const base = extractedSub ?? sliceOuterJson(rawStr) ?? rawStr;
    const sanitized = sanitizeJsonLike(base, { quoteKeys: true });
    if (sanitized !== base) repaired = true;
    const candidate = JSON.parse(sanitized);
    const z = tryZodValidate(candidate);
    if (z.ok) {
      return { ok: true, data: normalizeModelData(z.data), meta: { attempts, repaired, errors } };
    }
    errors.push(`Zod validation failed after quoting keys: ${z.error}`);
  } catch (e) {
    repaired = true;
    errors.push(`Quote-keys/parse failed: ${(e as Error)?.message ?? String(e)}`);
  }

  // Strategy 4: JSON5 (optional, skipped if not installed)
  // Note: json5 is not in deps by default; skip silently.
  // If added later, this can be enabled with a dynamic import.

  // Respect retries cap (we have already performed multiple attempts)
  if (attempts >= Math.max(1, retries)) {
    return { ok: false, meta: { attempts, repaired, errors } };
  }

  return { ok: false, meta: { attempts, repaired, errors } };
}

// ------------ Provider payload extraction (envelopes) ------------

function extractTextPayload(provider: ProviderName, raw: unknown): string {
  if (provider === 'openai') {
    const parsed = openAIChatSchema().parse(raw);
    return parsed.choices[0].message.content;
  }
  if (provider === 'anthropic') {
    const parsed = anthropicSchema().parse(raw);
    return parsed.content[0].text;
  }
  // google
  const parsed = geminiSchema().parse(raw);
  const parts = parsed.candidates[0]?.content?.parts ?? [];
  const firstWithText = parts.find(
    (p: { text?: string }) => typeof p.text === 'string' && ((p.text?.length ?? 0) > 0)
  );
  if (!firstWithText || !firstWithText.text) {
    throw new Error('No text part found in Gemini response');
  }
  return firstWithText.text;
}

// ------------ Mapping to application-level AnalysisResponse (legacy) ------------

function mapIssueTypeToLegacy(t: z.infer<typeof IssueTypeEnum>): ContinuityIssue['type'] {
  switch (t) {
    case 'pronoun_reference':
      return 'pronoun';
    case 'timeline':
      return 'timeline';
    case 'character_knowledge':
      return 'character';
    case 'other':
    default:
      return 'context';
  }
}

function mapSeverityToLegacy(s: z.infer<typeof SeverityEnum>): ContinuityIssue['severity'] {
  switch (s) {
    case 'low':
      return 'consider';
    case 'medium':
      return 'should-fix';
    case 'high':
    case 'critical':
    default:
      return 'must-fix';
  }
}

function modelToLegacyIssues(data: ModelAnalysisResponse): ContinuityIssue[] {
  return (data.issues ?? []).map((i: ModelAnalysisResponse['issues'][number]) => {
    const start = i.span?.start_index ?? 0;
    const end = i.span?.end_index ?? Math.max(0, start);
    return {
      type: mapIssueTypeToLegacy(i.type),
      severity: mapSeverityToLegacy(i.severity),
      description: i.explanation ?? '',
      textSpan: [Math.max(0, Math.trunc(start)), Math.max(0, Math.trunc(end))],
      suggestedFix: i.suggested_fix ? String(i.suggested_fix) : undefined,
    };
  });
}

// ------------ Public API (overloaded) ------------

export function validateAndNormalize(
  raw: unknown,
  options?: { retries?: number }
): { data: ModelAnalysisResponse; meta: ValidationMeta };
export function validateAndNormalize(
  provider: ProviderName,
  raw: unknown,
  fallbackModelLabel: string
): AppAnalysisResponse;
// Implementation
export function validateAndNormalize(
  a: unknown,
  b?: unknown,
  c?: unknown
): { data: ModelAnalysisResponse; meta: ValidationMeta } | AppAnalysisResponse {
  // Overload dispatcher
  if (isProviderName(a)) {
    // Legacy API: (provider, raw, fallbackModelLabel) -> AppAnalysisResponse
    const provider = a as ProviderName;
    const raw = b as unknown;
    const fallbackModelLabel = String(c ?? '');
    try {
      const payloadText = extractTextPayload(provider, raw);
      const result = parseModelOutputToZod(payloadText, 4);
      if (!result.ok) {
        throw new ValidationError(provider, 'Response validation failed');
      }
      const modelData = result.data; // already normalized and confidences filled
      const legacy: AppAnalysisResponse = {
        issues: modelToLegacyIssues(modelData),
        metadata: {
          modelUsed: fallbackModelLabel,
          provider,
          costEstimate: 0,
          durationMs: 0,
          confidence: typeof modelData.confidence === 'number' ? modelData.confidence : 0,
          cached: false,
        },
      };
      return legacy;
    } catch {
      throw new ValidationError(a as ProviderName, 'Response validation failed');
    }
  } else {
    // New primary API: (raw, options?) -> { data, meta }
    const retries = isObject(b) && isNumberFinite((b as any).retries) ? Math.max(1, Math.trunc((b as any).retries)) : 4;
    const result = parseModelOutputToZod(a, retries);
    if (!result.ok) {
      return { data: { issues: [], summary: '', confidence: 0 }, meta: result.meta };
    }
    return { data: result.data, meta: result.meta };
  }
}

// Tokenizers.ts
// Pure utilities for token estimation with optional dynamic tokenizer support.
// - No hard dependency on tokenizer libraries; best-effort dynamic import attempted without logging.
// - Deterministic outputs for the same inputs (no randomness).
//
// Encoding notes (OpenAI):
// - GPT-3.5/4/4o/gpt-* typically use cl100k_base; o-series may use o200k_base where available.
// - We map broadly and fall back to heuristics if encodings/libs are unavailable.
//
// Claude/Gemini:
// - No official JS tokenizer readily available; we approximate with chars-per-token heuristics when dynamic libs aren't present.
//
// Heuristic fallback (used if tokenizer not available):
//   tokens = max(1, round((asciiLen + 0.5*nonAsciiCount) / divisor))
//   divisors by provider/model family:
//     - OpenAI: 4.0
//     - Claude: 3.5
//     - Gemini: 3.2

type MessageLike = { role?: string; content: string };

// Small overhead configuration for chat-like messages
const OVERHEAD_PER_MESSAGE = 4;
const BASE_OVERHEAD = 2;

function normalizeModelId(modelId: string): string {
  return String(modelId || '').toLowerCase().trim();
}

function modelFamily(modelId: string): 'openai' | 'anthropic' | 'google' | 'unknown' {
  const m = normalizeModelId(modelId);
  if (m.includes('gpt-') || m.startsWith('gpt') || m.startsWith('o1') || m.startsWith('o3')) return 'openai';
  if (m.includes('claude') || m.includes('anthropic')) return 'anthropic';
  if (m.includes('gemini') || m.includes('google')) return 'google';
  return 'unknown';
}

// Map model to preferred encoding id for js-tiktoken-like libs
function preferredEncodingName(modelId: string): 'cl100k_base' | 'o200k_base' | 'cl100k_base' {
  const m = normalizeModelId(modelId);
  // Heuristic mapping:
  // - Newer "o" and "4o" families prefer o200k_base when available.
  if (m.includes('o1') || m.includes('o3') || m.includes('4o')) return 'o200k_base';
  // Default OpenAI mapping
  return 'cl100k_base';
}

// Attempts to get a length function via dynamic import of a tokenizer library.
// Synchronous API; returns a function or null. We DO NOT await dynamic import.
// Future calls may benefit if the dynamic import has resolved by then.
let tiktokenModule: any | null = null;
let tiktokenTried = false;

function tryInitiateDynamicImport(): void {
  if (tiktokenTried) return;
  tiktokenTried = true;
  try {
    // Use non-literal dynamic specifiers so TypeScript doesn't try to resolve missing modules.
    const m1 = 'js-tiktoken';
     
    (import(m1 as any) as Promise<any>)
      .then((mod: any) => {
        tiktokenModule = mod;
      })
      .catch(() => {
        const m2 = '@dqbd/tiktoken';
         
        return (import(m2 as any) as Promise<any>)
          .then((mod: any) => {
            tiktokenModule = mod;
          })
          .catch(() => {});
      });
  } catch {}
}

// Get an encoder length function for a given model if the module has loaded.
function getEncoderLenIfReady(modelId: string): ((text: string) => number) | null {
  if (!tiktokenModule) return null;
  const encName = preferredEncodingName(modelId);
  try {
    // js-tiktoken exposes get_encoding(name)
    if (typeof tiktokenModule.get_encoding === 'function') {
      const enc = tiktokenModule.get_encoding(encName);
      if (enc && typeof enc.encode === 'function') {
        return (text: string) => {
          try {
            return enc.encode(text ?? '').length || 0;
          } finally {
            // Some encoders have a free() API; guard it if present
            if (typeof enc.free === 'function') {
              try { enc.free(); } catch {}
            }
          }
        };
      }
    }
    // @dqbd/tiktoken may expose encodingForModel(model)
    if (typeof tiktokenModule.encodingForModel === 'function') {
      const enc = tiktokenModule.encodingForModel(encName);
      if (enc && typeof enc.encode === 'function') {
        return (text: string) => enc.encode(text ?? '').length || 0;
      }
    }
  } catch {}
  return null;
}

function countAsciiAndNonAscii(s: string): { ascii: number; nonAscii: number } {
  let ascii = 0;
  let nonAscii = 0;
  for (let i = 0; i < s.length; i++) {
    const code = s.charCodeAt(i);
    if (code <= 0x7f) ascii++;
    else nonAscii++;
  }
  return { ascii, nonAscii };
}

function heuristicDivisor(modelId: string): number {
  switch (modelFamily(modelId)) {
    case 'openai':
      return 4.0;
    case 'anthropic':
      return 3.5;
    case 'google':
      return 3.2;
    default:
      return 4.0;
  }
}

function heuristicTokens(modelId: string, text: string): number {
  const divisor = heuristicDivisor(modelId);
  const { ascii, nonAscii } = countAsciiAndNonAscii(text ?? '');
  const estimate = Math.round((ascii + 0.5 * nonAscii) / divisor);
  return Math.max(1, estimate);
}

// Public API

export function estimateTokensForModel(modelId: string, text: string): number {
  // Lazy-start dynamic import without side effects outside of this function.
  tryInitiateDynamicImport();
  const encoderLen = getEncoderLenIfReady(modelId);
  if (encoderLen) {
    try {
      const n = encoderLen(text ?? '');
      if (Number.isFinite(n) && n > 0) return n;
    } catch {}
  }
  // Claude and Gemini: approximate via chars/token ratio when tokenizer is absent
  return heuristicTokens(modelId, text ?? '');
}

export function estimateMessageTokens(modelId: string, parts: Array<MessageLike>): number {
  const list = Array.isArray(parts) ? parts : [];
  let total = BASE_OVERHEAD;
  for (const p of list) {
    const content = p?.content ?? '';
    total += estimateTokensForModel(modelId, content) + OVERHEAD_PER_MESSAGE;
  }
  return Math.max(1, total | 0);
}

// Expose constants for tests (not side-effectful)
export const __TEST_ONLY__TOKENIZER_CONSTANTS = {
  OVERHEAD_PER_MESSAGE,
  BASE_OVERHEAD,
};

/**
 * CacheStorage
 *
 * Two-tier cache:
 *  - L1: in-memory LRU (lru-cache if available; Map-based fallback)
 *  - L2: SQLite via better-sqlite3 (optional, dynamic require to avoid bundler/runtime issues)
 *
 * Notes:
 *  - 24h TTL enforced at read-time for both tiers.
 *  - L2 is best-effort. If unavailable or errors occur, storage operates in L1-only mode.
 *  - JSON serialization must handle ReaderKnowledge.knownCharacters (Set) safely.
 *  - Provides public underscore test hooks that delegate to private helpers.
 */

import path from 'path';
import type { CachedAnalysis, CacheStats } from './types';

// Duplication of TTL to avoid coupling/circular import. Keep in sync with AnalysisCache.
const TTL_MS = 24 * 60 * 60 * 1000;
const L1_MAX = 200;
const L2_MAX_ROWS = 1000;

// Dynamic require helper
const dynamicRequire: NodeRequire | null = (() => {
  try {
     
    return eval('require');
  } catch {
    return null;
  }
})();

type L1Value = CachedAnalysis;
type L1Key = string;

/**
 * Minimal LRU compatible interface used internally so we can swap implementations.
 */
interface LruLike<K, V> {
  get(key: K): V | undefined;
  set(key: K, val: V): void;
  has(key: K): boolean;
  delete(key: K): boolean;
  clear(): void;
  readonly size: number;
  /** Optional keys iterator for maintenance/invalidation if available. */
  keys?(): IterableIterator<K>;
}

/**
 * Map-based fallback LRU with simple recency management:
 *  - get: reinsert to end
 *  - set: evict first inserted if over capacity
 */
class SimpleMapLRU<K, V> implements LruLike<K, V> {
  private map = new Map<K, V>();
  private max: number;
  constructor(max: number) { this.max = max; }
  get(key: K): V | undefined {
    if (!this.map.has(key)) return undefined;
    const val = this.map.get(key)!;
    this.map.delete(key);
    this.map.set(key, val);
    return val;
  }
  set(key: K, val: V): void {
    if (this.map.has(key)) this.map.delete(key);
    this.map.set(key, val);
    if (this.map.size > this.max) {
      const firstKey = this.map.keys().next().value;
      if (firstKey !== undefined) this.map.delete(firstKey);
    }
  }
  has(key: K): boolean { return this.map.has(key); }
  delete(key: K): boolean { return this.map.delete(key); }
  clear(): void { this.map.clear(); }
  get size(): number { return this.map.size; }
  keys(): IterableIterator<K> { return this.map.keys(); }
}

// Attempt to build an L1 cache using lru-cache if available.
function createL1(max: number): LruLike<L1Key, L1Value> {
  try {
    if (dynamicRequire) {
      const LRU = dynamicRequire('lru-cache');
      if (LRU && LRU.default) {
        // lru-cache v7+ exports default
        const lru = new LRU.default({ max });
        return {
          get: (k: L1Key) => lru.get(k),
          set: (k: L1Key, v: L1Value) => { lru.set(k, v); },
          has: (k: L1Key) => lru.has(k),
          delete: (k: L1Key) => lru.delete(k),
          clear: () => lru.clear(),
          get size() { return lru.size; },
          keys: () => lru.keys(),
        };
      } else if (LRU) {
        // older versions
        const lru = new LRU({ max });
        return {
          get: (k: L1Key) => lru.get(k),
          set: (k: L1Key, v: L1Value) => { lru.set(k, v); },
          has: (k: L1Key) => lru.has(k),
          delete: (k: L1Key) => lru.delete(k),
          clear: () => lru.clear(),
          get size() { return lru.size; },
          keys: () => lru.keys(),
        };
      }
    }
  } catch {
    // ignore
  }
  return new SimpleMapLRU<L1Key, L1Value>(max);
}

// SQLite (better-sqlite3) dynamic load
type BetterSqliteDatabase = any;

function resolveDbPath(): string {
  // Try Electron userData
  try {
    if (dynamicRequire) {
      const electron = dynamicRequire('electron');
      const userData =
        electron?.app?.getPath?.('userData') ||
        (electron as any)?.remote?.app?.getPath?.('userData');
      if (typeof userData === 'string' && userData.length > 0) {
        return path.join(userData, 'analysis-cache.sqlite');
      }
    }
  } catch {
    // ignore
  }
  // Fallback to CWD
  return path.join(process.cwd(), 'analysis-cache.sqlite');
}

// Helpers to serialize/deserialize CachedAnalysis safely for DB storage
function serializeCachedAnalysis(value: CachedAnalysis): string {
  try {
    // Clone and convert ReaderKnowledge.knownCharacters(Set) -> Array to ensure JSON-safe
    const clone: any = JSON.parse(JSON.stringify(value, (_k, v) => {
      // Basic stable conversion (structures already JSON-safe except Set)
      return v;
    }));
    if (clone?.analysis?.readerContext?.knownCharacters instanceof Set) {
      clone.analysis.readerContext.knownCharacters = Array.from(clone.analysis.readerContext.knownCharacters);
    } else if (Array.isArray(clone?.analysis?.readerContext?.knownCharacters)) {
      // already array, ok
    } else if (value?.analysis?.readerContext?.knownCharacters instanceof Set) {
      clone.analysis.readerContext.knownCharacters = Array.from(value.analysis.readerContext.knownCharacters);
    }
    return JSON.stringify(clone);
  } catch {
    // As a last resort, do a manual shallow conversion for knownCharacters
    try {
      const shallow: any = {
        ...value,
        analysis: {
          ...value.analysis,
          readerContext: {
            ...value.analysis.readerContext,
            knownCharacters: Array.from(value.analysis.readerContext.knownCharacters || []),
          },
        },
      };
      return JSON.stringify(shallow);
    } catch {
      // give up but avoid throwing
      return '';
    }
  }
}

function deserializeCachedAnalysis(json: string): CachedAnalysis | null {
  try {
    const parsed = JSON.parse(json);
    if (parsed?.analysis?.readerContext?.knownCharacters && Array.isArray(parsed.analysis.readerContext.knownCharacters)) {
      parsed.analysis.readerContext.knownCharacters = new Set<string>(parsed.analysis.readerContext.knownCharacters);
    }
    return parsed as CachedAnalysis;
  } catch {
    return null;
  }
}

interface SqliteRow {
  cache_key: string;
  analysis_data: string;
  semantic_hash: string;
  cached_at: number;
  last_accessed: number;
  hit_count: number;
  scene_id?: string | null;
  position?: number | null;
}

export default class CacheStorage {
  private l1: LruLike<L1Key, L1Value>;
  private l2Db: BetterSqliteDatabase | null = null;
  private l2Available = false;

  // In-memory metadata for L1 invalidation
  private keyMeta = new Map<string, { sceneId?: string; position?: number }>();

  // Storage-level approximate stats
  private storageHits = 0;
  private storageMisses = 0;

  constructor() {
    this.l1 = createL1(L1_MAX);
  }

  async init(): Promise<void> {
    // initialize SQLite if possible
    try {
      let Database: any = null;
      if (dynamicRequire) {
        try {
          Database = dynamicRequire('better-sqlite3');
        } catch {
          Database = null;
        }
      }

      if (Database) {
        const dbPath = resolveDbPath();
        this.l2Db = new Database(dbPath);
        this.l2Available = true;
        // Create schema
        this.l2Db
          .prepare(
            `CREATE TABLE IF NOT EXISTS analysis_cache (
              cache_key TEXT PRIMARY KEY,
              analysis_data TEXT NOT NULL,
              semantic_hash TEXT NOT NULL,
              cached_at INTEGER NOT NULL,
              last_accessed INTEGER NOT NULL,
              hit_count INTEGER DEFAULT 0,
              scene_id TEXT,
              position INTEGER
            )`,
          )
          .run();

        this.l2Db.prepare(`CREATE INDEX IF NOT EXISTS idx_scene_id ON analysis_cache(scene_id)`).run();
        this.l2Db.prepare(`CREATE INDEX IF NOT EXISTS idx_position ON analysis_cache(position)`).run();
        this.l2Db.prepare(`CREATE INDEX IF NOT EXISTS idx_last_accessed ON analysis_cache(last_accessed)`).run();
      }
    } catch (err) {
      // Disable L2 for session
      this.l2Available = false;
      this.l2Db = null;
       
      console.warn('[CacheStorage] SQLite unavailable, continuing with memory-only.', err);
    }
  }

  // Public API

  async get(cacheKey: string): Promise<CachedAnalysis | null> {
    try {
      // L1
      const fromMem = this.getFromMemory(cacheKey);
      if (fromMem) {
        this.storageHits++;
        this.bumpAccess(cacheKey, /*l1*/ true, /*l2*/ true);
        return fromMem;
      }
      this.storageMisses++;

      // L2
      const row = await this.getRowFromSQLite(cacheKey);
      if (row) {
        const now = Date.now();
        if (now - row.cached_at > TTL_MS) {
          // expired
          await this.deleteFromSQLite(cacheKey);
          return null;
        }
        const parsed = deserializeCachedAnalysis(row.analysis_data);
        if (!parsed) {
          // corruption
          await this.deleteFromSQLite(cacheKey);
          return null;
        }
        // hydrate L1 and metadata
        this.setToMemory(cacheKey, parsed);
        this.keyMeta.set(cacheKey, { sceneId: row.scene_id ?? undefined, position: row.position ?? undefined });
        this.storageHits++;
        this.bumpAccess(cacheKey, /*l1*/ true, /*l2*/ true);
        return parsed;
      }
      return null;
    } catch {
      // Never break caller flow
      return null;
    }
  }

  // meta is optional; if provided, allows L2 to store scene_id/position for targeted invalidation.
  async set(cacheKey: string, analysis: CachedAnalysis, meta?: { sceneId?: string; position?: number }): Promise<void> {
    try {
      // L1
      this.setToMemory(cacheKey, analysis);
      if (meta) this.keyMeta.set(cacheKey, { sceneId: meta.sceneId, position: meta.position });

      // L2
      await this._setToSQLite(cacheKey, analysis, meta);
      await this.cleanupExpired();
      await this.enforceL2Capacity();
    } catch (err) {
      // Disable L2 on persistent failure, do not throw
       
      console.warn('[CacheStorage] set failed, proceeding with memory-only.', err);
      this.l2Available = false;
      this.l2Db = null;
    }
  }

  async getStats(): Promise<CacheStats> {
    let l2Count = 0;
    if (this.l2Available && this.l2Db) {
      try {
        const row = this.l2Db.prepare('SELECT COUNT(*) as cnt FROM analysis_cache').get();
        l2Count = typeof row?.cnt === 'number' ? row.cnt : 0;
      } catch {
        // ignore
      }
    }

    const totalGets = this.storageHits + this.storageMisses;
    const hitRate = totalGets > 0 ? (this.storageHits / totalGets) * 100 : 0;

    return {
      hitRate,
      size: (this.l1?.size ?? 0) + l2Count,
      totalHits: this.storageHits,
      totalMisses: this.storageMisses,
      avgHitTime: 0,
      avgGenerationTime: 0,
    };
    }

  async clear(): Promise<void> {
    try {
      this.l1.clear();
      this.keyMeta.clear();
      if (this.l2Available && this.l2Db) {
        this.l2Db.prepare('DELETE FROM analysis_cache').run();
      }
    } catch {
      // ignore
    }
  }

  // Private L1 helpers

  private getFromMemory(cacheKey: string): CachedAnalysis | null {
    try {
      if (!this.l1?.has(cacheKey)) return null;
      const val = this.l1.get(cacheKey);
      if (!val) return null;
      const now = Date.now();
      // Enforce TTL against creation time
      if (now - val.cachedAt > TTL_MS) {
        this.l1.delete(cacheKey);
        this.keyMeta.delete(cacheKey);
        return null;
      }
      return val;
    } catch {
      return null;
    }
  }

  private setToMemory(cacheKey: string, analysis: CachedAnalysis): void {
    try {
      this.l1.set(cacheKey, analysis);
    } catch {
      // ignore
    }
  }

  // Private L2 helpers

  private async getRowFromSQLite(cacheKey: string): Promise<SqliteRow | null> {
    if (!this.l2Available || !this.l2Db) return null;
    try {
      const row = this.l2Db
        .prepare(
          `SELECT cache_key, analysis_data, semantic_hash, cached_at, last_accessed, hit_count, scene_id, position
           FROM analysis_cache WHERE cache_key = ?`,
        )
        .get(cacheKey) as SqliteRow | undefined;
      return row || null;
    } catch (err) {
      // Disable L2 on error
      this.l2Available = false;
      this.l2Db = null;
       
      console.warn('[CacheStorage] getRowFromSQLite failed, disabling L2.', err);
      return null;
    }
  }

  // Test hook: publicly exposed wrapper
  public async _getFromSQLite(cacheKey: string): Promise<CachedAnalysis | null> {
    const row = await this.getRowFromSQLite(cacheKey);
    if (!row) return null;
    const parsed = deserializeCachedAnalysis(row.analysis_data);
    if (!parsed) return null;
    return parsed;
  }

  private async setToSQLite(cacheKey: string, analysis: CachedAnalysis, meta?: { sceneId?: string; position?: number }): Promise<void> {
    if (!this.l2Available || !this.l2Db) return;
    try {
      const payload = serializeCachedAnalysis(analysis);
      if (!payload) return; // skip if serialization failed
      const now = Date.now();
      const sceneId = meta?.sceneId ?? null;
      const position = typeof meta?.position === 'number' ? meta!.position : null;

      this.l2Db
        .prepare(
          `INSERT INTO analysis_cache (cache_key, analysis_data, semantic_hash, cached_at, last_accessed, hit_count, scene_id, position)
           VALUES (@cache_key, @analysis_data, @semantic_hash, @cached_at, @last_accessed, @hit_count, @scene_id, @position)
           ON CONFLICT(cache_key) DO UPDATE SET
             analysis_data = excluded.analysis_data,
             semantic_hash = excluded.semantic_hash,
             cached_at = excluded.cached_at,
             last_accessed = excluded.last_accessed,
             scene_id = COALESCE(excluded.scene_id, analysis_cache.scene_id),
             position = COALESCE(excluded.position, analysis_cache.position)`,
        )
        .run({
          cache_key: cacheKey,
          analysis_data: payload,
          semantic_hash: analysis.semanticHash,
          cached_at: analysis.cachedAt,
          last_accessed: analysis.lastAccessed,
          hit_count: analysis.hitCount,
          scene_id: sceneId,
          position: position,
        });
    } catch (err) {
      // Disable L2 on persistent failure
      this.l2Available = false;
      this.l2Db = null;
       
      console.warn('[CacheStorage] setToSQLite failed, disabling L2.', err);
    }
  }

  // Test hook: publicly exposed wrapper
  public async _setToSQLite(cacheKey: string, analysis: CachedAnalysis, meta?: { sceneId?: string; position?: number }): Promise<void> {
    await this.setToSQLite(cacheKey, analysis, meta);
  }

  private async deleteFromSQLite(cacheKey: string): Promise<void> {
    if (!this.l2Available || !this.l2Db) return;
    try {
      this.l2Db.prepare('DELETE FROM analysis_cache WHERE cache_key = ?').run(cacheKey);
    } catch (err) {
      // Disable L2 on error
      this.l2Available = false;
      this.l2Db = null;
       
      console.warn('[CacheStorage] deleteFromSQLite failed, disabling L2.', err);
    }
  }

  private async enforceL2Capacity(): Promise<void> {
    if (!this.l2Available || !this.l2Db) return;
    try {
      const countRow = this.l2Db.prepare('SELECT COUNT(*) as cnt FROM analysis_cache').get();
      const cnt = typeof countRow?.cnt === 'number' ? countRow.cnt : 0;
      const excess = cnt - L2_MAX_ROWS;
      if (excess > 0) {
        this.l2Db
          .prepare(
            `DELETE FROM analysis_cache
             WHERE cache_key IN (
               SELECT cache_key FROM analysis_cache
               ORDER BY last_accessed ASC
               LIMIT ?
             )`,
          )
          .run(excess);
      }
    } catch (err) {
      // ignore; not critical
       
      console.warn('[CacheStorage] enforceL2Capacity warning:', err);
    }
  }

  private async cleanupExpired(): Promise<void> {
    if (!this.l2Available || !this.l2Db) return;
    try {
      const cutoff = Date.now() - TTL_MS;
      this.l2Db.prepare('DELETE FROM analysis_cache WHERE cached_at < ?').run(cutoff);
    } catch (err) {
      // ignore
       
      console.warn('[CacheStorage] cleanupExpired warning:', err);
    }
  }

  private bumpAccess(cacheKey: string, bumpL1: boolean, bumpL2: boolean): void {
    const now = Date.now();
    if (bumpL1) {
      const val = this.l1.get(cacheKey);
      if (val) {
        const bumped: CachedAnalysis = {
          ...val,
          lastAccessed: now,
          hitCount: (val.hitCount || 0) + 1,
        };
        this.l1.set(cacheKey, bumped);
      }
    }
    if (bumpL2 && this.l2Available && this.l2Db) {
      try {
        this.l2Db
          .prepare('UPDATE analysis_cache SET last_accessed = ?, hit_count = hit_count + 1 WHERE cache_key = ?')
          .run(now, cacheKey);
      } catch {
        // ignore
      }
    }
  }

  // Private invalidation helpers (invoked by AnalysisCache using symbolic helper call with "as any")

  private deleteBySceneId(sceneId: string): number {
    let removed = 0;
    // L1 by metadata map
    try {
      for (const [key, meta] of this.keyMeta.entries()) {
        if (meta.sceneId === sceneId) {
          if (this.l1.delete(key)) removed++;
          this.keyMeta.delete(key);
        }
      }
    } catch {
      // ignore
    }
    // L2
    try {
      if (this.l2Available && this.l2Db) {
        const info = this.l2Db.prepare('DELETE FROM analysis_cache WHERE scene_id = ?').run(sceneId);
        removed += info?.changes ?? 0;
      }
    } catch {
      // ignore
    }
    return removed;
  }

  private deleteByPosition(position: number): number {
    let removed = 0;
    // L1 by metadata map
    try {
      for (const [key, meta] of this.keyMeta.entries()) {
        if (typeof meta.position === 'number' && meta.position === position) {
          if (this.l1.delete(key)) removed++;
          this.keyMeta.delete(key);
        }
      }
    } catch {
      // ignore
    }
    // L2
    try {
      if (this.l2Available && this.l2Db) {
        const info = this.l2Db.prepare('DELETE FROM analysis_cache WHERE position = ?').run(position);
        removed += info?.changes ?? 0;
      }
    } catch {
      // ignore
    }
    return removed;
  }

  // Public underscore test hooks for memory
  public _getFromMemory(cacheKey: string): CachedAnalysis | null {
    return this.getFromMemory(cacheKey);
  }
}

import type { Manuscript, Scene, RewriteVersion } from '../../shared/types';
import { promises as fs } from 'fs';
import * as path from 'path';

export interface ExportOptions {
  format: 'original' | 'rewritten' | 'both' | 'changelog';
  includeMetadata: boolean;
  includeChangeLog: boolean;
  changeLogDetail: 'summary' | 'detailed';
  outputPath?: string;
  filename?: string;
}

export interface ExportResult {
  success: boolean;
  filePath?: string;
  error?: string;
  stats?: {
    totalScenes: number;
    rewrittenScenes: number;
    totalWords: number;
    totalChanges: number;
  };
}

export interface ChangeLogEntry {
  sceneNumber: number;
  sceneTitle: string;
  changes: {
    issueType: string;
    description: string;
    fix: string;
  }[];
  wordCountBefore: number;
  wordCountAfter: number;
  modelUsed?: string;
  timestamp?: number;
}

class ManuscriptExporter {
  /**
   * Export manuscript with various format options
   */
  async exportManuscript(
    manuscript: Manuscript,
    rewrites: Map<string, RewriteVersion[]>,
    options: ExportOptions
  ): Promise<ExportResult> {
    try {
      const stats = this.calculateStats(manuscript, rewrites);
      
      let content: string;
      switch (options.format) {
        case 'original':
          content = this.exportOriginal(manuscript);
          break;
        case 'rewritten':
          content = this.exportRewritten(manuscript, rewrites);
          break;
        case 'both':
          content = this.exportBothVersions(manuscript, rewrites, options);
          break;
        case 'changelog':
          content = this.exportChangeLog(manuscript, rewrites, options);
          break;
        default:
          content = this.exportRewritten(manuscript, rewrites);
      }
      
      // Add metadata header if requested
      if (options.includeMetadata) {
        content = this.addMetadataHeader(manuscript, stats, options) + content;
      }
      
      // Add change log section if requested
      if (options.includeChangeLog && options.format !== 'changelog') {
        content += this.generateChangeLogSection(manuscript, rewrites, options);
      }
      
      // Determine output path
      const outputPath = options.outputPath || process.cwd();
      const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
      const defaultFilename = `${manuscript.title || 'manuscript'}_${options.format}_${timestamp}.txt`;
      const filename = options.filename || defaultFilename;
      const filePath = path.join(outputPath, filename);
      
      // Write file
      await fs.writeFile(filePath, content, 'utf-8');
      
      return {
        success: true,
        filePath,
        stats
      };
      
    } catch (error) {
       
      console.error('[ManuscriptExporter] Export error:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Export failed'
      };
    }
  }
  
  private exportOriginal(manuscript: Manuscript): string {
    const orderedScenes = this.getOrderedScenes(manuscript);
    return orderedScenes
      .map(scene => scene.text)
      .join('\n\n### SCENE BREAK ###\n\n');
  }
  
  private exportRewritten(
    manuscript: Manuscript,
    rewrites: Map<string, RewriteVersion[]>
  ): string {
    const orderedScenes = this.getOrderedScenes(manuscript);
    
    return orderedScenes
      .map(scene => {
        // Use applied rewrite if available, otherwise original
        if (scene.rewriteStatus === 'approved') {
          // Scene text was already updated with rewrite
          return scene.text;
        }
        
        // Check for unapplied rewrites
        const sceneRewrites = rewrites.get(scene.id);
        const latestRewrite = sceneRewrites?.[sceneRewrites.length - 1];
        
        if (latestRewrite && latestRewrite.rewrittenText) {
          return latestRewrite.rewrittenText;
        }
        
        return scene.text;
      })
      .join('\n\n### SCENE BREAK ###\n\n');
  }
  
  private exportBothVersions(
    manuscript: Manuscript,
    rewrites: Map<string, RewriteVersion[]>,
    options: ExportOptions
  ): string {
    const orderedScenes = this.getOrderedScenes(manuscript);
    const sections: string[] = [];
    
    // Add table of contents
    sections.push('TABLE OF CONTENTS\n' + '='.repeat(50) + '\n');
    sections.push('Original Version ............... Page 1');
    sections.push('Rewritten Version .............. Page 2');
    if (options.includeChangeLog) {
      sections.push('Change Log ..................... Page 3');
    }
    sections.push('\n\n');
    
    // Original version
    sections.push('ORIGINAL VERSION\n' + '='.repeat(50) + '\n\n');
    sections.push(this.exportOriginal(manuscript));
    sections.push('\n\n' + '='.repeat(50) + '\n\n');
    
    // Rewritten version
    sections.push('REWRITTEN VERSION\n' + '='.repeat(50) + '\n\n');
    sections.push(this.exportRewritten(manuscript, rewrites));
    
    return sections.join('');
  }
  
  private exportChangeLog(
    manuscript: Manuscript,
    rewrites: Map<string, RewriteVersion[]>,
    options: ExportOptions
  ): string {
    const entries = this.buildChangeLogEntries(manuscript, rewrites);
    
    const sections: string[] = [
      'MANUSCRIPT CHANGE LOG',
      '='.repeat(50),
      `Generated: ${new Date().toLocaleString()}`,
      `Total Scenes: ${manuscript.scenes.length}`,
      `Scenes Rewritten: ${entries.length}`,
      '',
      '='.repeat(50),
      ''
    ];
    
    if (entries.length === 0) {
      sections.push('No scenes have been rewritten.');
      return sections.join('\n');
    }
    
    // Add entries
    entries.forEach((entry, index) => {
      sections.push(`\nSCENE ${entry.sceneNumber}`);
      sections.push('-'.repeat(30));
      
      if (entry.sceneTitle) {
        sections.push(`Title: ${entry.sceneTitle}`);
      }
      
      sections.push(`Word Count: ${entry.wordCountBefore} ‚Üí ${entry.wordCountAfter} (${
        entry.wordCountAfter - entry.wordCountBefore > 0 ? '+' : ''
      }${entry.wordCountAfter - entry.wordCountBefore})`);
      
      if (entry.modelUsed) {
        sections.push(`AI Model: ${entry.modelUsed}`);
      }
      
      sections.push('\nChanges Made:');
      entry.changes.forEach(change => {
        sections.push(`  ‚Ä¢ ${change.issueType.toUpperCase()}: ${change.description}`);
        if (options.changeLogDetail === 'detailed' && change.fix) {
          sections.push(`    ‚Üí ${change.fix}`);
        }
      });
      
      if (index < entries.length - 1) {
        sections.push('');
      }
    });
    
    return sections.join('\n');
  }
  
  private generateChangeLogSection(
    manuscript: Manuscript,
    rewrites: Map<string, RewriteVersion[]>,
    options: ExportOptions
  ): string {
    const changeLog = this.exportChangeLog(manuscript, rewrites, options);
    return '\n\n' + '='.repeat(50) + '\n\n' + changeLog;
  }
  
  private addMetadataHeader(
    manuscript: Manuscript,
    stats: ExportResult['stats'],
    options: ExportOptions
  ): string {
    const header: string[] = [
      '='.repeat(50),
      'MANUSCRIPT EXPORT',
      '='.repeat(50),
      `Title: ${manuscript.title || 'Untitled'}`,
      `Export Date: ${new Date().toLocaleString()}`,
      `Export Format: ${options.format}`,
      `Total Scenes: ${stats?.totalScenes || 0}`,
      `Rewritten Scenes: ${stats?.rewrittenScenes || 0}`,
      `Total Words: ${stats?.totalWords || 0}`,
      '='.repeat(50),
      '\n\n'
    ];
    
    return header.join('\n');
  }
  
  private buildChangeLogEntries(
    manuscript: Manuscript,
    rewrites: Map<string, RewriteVersion[]>
  ): ChangeLogEntry[] {
    const entries: ChangeLogEntry[] = [];
    const orderedScenes = this.getOrderedScenes(manuscript);
    
    orderedScenes.forEach((scene, index) => {
      const sceneRewrites = rewrites.get(scene.id);
      const latestRewrite = sceneRewrites?.[sceneRewrites.length - 1];
      
      if (latestRewrite && (scene.rewriteStatus === 'approved' || latestRewrite.rewrittenText)) {
        const entry: ChangeLogEntry = {
          sceneNumber: index + 1,
          sceneTitle: this.extractSceneTitle(scene.text),
          changes: latestRewrite.issuesAddressed.map(issue => ({
            issueType: issue.type,
            description: issue.description,
            fix: issue.suggestedFix || 'Applied contextual fix'
          })),
          wordCountBefore: scene.wordCount,
          wordCountAfter: this.countWords(
            scene.rewriteStatus === 'approved' ? scene.text : latestRewrite.rewrittenText
          ),
          modelUsed: latestRewrite.modelUsed,
          timestamp: latestRewrite.timestamp
        };
        
        entries.push(entry);
      }
    });
    
    return entries;
  }
  
  private getOrderedScenes(manuscript: Manuscript): Scene[] {
    return manuscript.currentOrder
      .map(id => manuscript.scenes.find(s => s.id === id))
      .filter(Boolean) as Scene[];
  }
  
  private calculateStats(
    manuscript: Manuscript,
    rewrites: Map<string, RewriteVersion[]>
  ): ExportResult['stats'] {
    const rewrittenScenes = manuscript.scenes.filter(scene => 
      scene.rewriteStatus === 'approved' || rewrites.has(scene.id)
    );
    
    const totalWords = manuscript.scenes.reduce((sum, scene) => {
      if (scene.rewriteStatus === 'approved') {
        return sum + scene.wordCount;
      }
      const rewrite = rewrites.get(scene.id)?.[0];
      if (rewrite?.rewrittenText) {
        return sum + this.countWords(rewrite.rewrittenText);
      }
      return sum + scene.wordCount;
    }, 0);
    
    const totalChanges = Array.from(rewrites.values())
      .reduce((sum, versions) => {
        const latest = versions[versions.length - 1];
        return sum + (latest?.issuesAddressed.length || 0);
      }, 0);
    
    return {
      totalScenes: manuscript.scenes.length,
      rewrittenScenes: rewrittenScenes.length,
      totalWords,
      totalChanges
    };
  }
  
  private extractSceneTitle(text: string): string {
    const firstLine = text.split('\n')[0];
    if (firstLine && firstLine.length < 100) {
      return firstLine.trim();
    }
    return text.substring(0, 50).trim() + '...';
  }
  
  private countWords(text: string): number {
    return text.split(/\s+/).filter(word => word.length > 0).length;
  }
}

export default ManuscriptExporter;

import type { BatchRewriteProgress } from './RewriteOrchestrator';

export interface PerformanceMetrics {
  avgRewriteTime: number;
  cacheHitRate: number;
  memoryUsage: number;
  errorRate: number;
  lastUpdated: number;
}

class PerformanceOptimizer {
  private metrics: PerformanceMetrics = {
    avgRewriteTime: 0,
    cacheHitRate: 0,
    memoryUsage: 0,
    errorRate: 0,
    lastUpdated: Date.now()
  };
  
  private rewriteTimes: number[] = [];
  private cacheHits = 0;
  private cacheMisses = 0;
  private errors = 0;
  private total = 0;
  
  /**
   * Track rewrite performance
   */
  trackRewrite(startTime: number, success: boolean, cached: boolean = false): void {
    const duration = Date.now() - startTime;
    this.total++;
    
    if (success) {
      this.rewriteTimes.push(duration);
      // Keep only last 100 times for moving average
      if (this.rewriteTimes.length > 100) {
        this.rewriteTimes.shift();
      }
    } else {
      this.errors++;
    }
    
    if (cached) {
      this.cacheHits++;
    } else {
      this.cacheMisses++;
    }
    
    this.updateMetrics();
  }
  
  /**
   * Get current performance metrics
   */
  getMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }
  
  /**
   * Suggest optimizations based on current metrics
   */
  getSuggestions(): string[] {
    const suggestions: string[] = [];
    
    if (this.metrics.avgRewriteTime > 30000) {
      suggestions.push('Consider using simpler models for basic issues');
    }
    
    if (this.metrics.cacheHitRate < 0.3 && this.total > 10) {
      suggestions.push('Low cache hit rate - ensure cache is properly configured');
    }
    
    if (this.metrics.errorRate > 0.1) {
      suggestions.push('High error rate detected - check API keys and network');
    }
    
    if (this.metrics.memoryUsage > 500) {
      suggestions.push('High memory usage - consider clearing old rewrites');
    }
    
    return suggestions;
  }
  
  /**
   * Optimize batch size based on performance
   */
  getOptimalBatchSize(): number {
    if (this.metrics.avgRewriteTime < 5000) {
      return 10; // Fast processing, larger batches OK
    } else if (this.metrics.avgRewriteTime < 15000) {
      return 5; // Medium speed
    } else {
      return 3; // Slow processing, smaller batches
    }
  }
  
  /**
   * Clean up old data to free memory
   */
  cleanup(): void {
    // Clear old metrics
    if (this.rewriteTimes.length > 100) {
      this.rewriteTimes = this.rewriteTimes.slice(-100);
    }
    
    // Reset counters periodically
    if (this.total > 1000) {
      const hitRate = this.metrics.cacheHitRate;
      const errorRate = this.metrics.errorRate;
      
      // Reset but maintain ratios
      this.cacheHits = Math.round(hitRate * 100);
      this.cacheMisses = Math.round((1 - hitRate) * 100);
      this.errors = Math.round(errorRate * 100);
      this.total = 100;
    }
  }
  
  private updateMetrics(): void {
    // Calculate average rewrite time
    if (this.rewriteTimes.length > 0) {
      const sum = this.rewriteTimes.reduce((a, b) => a + b, 0);
      this.metrics.avgRewriteTime = Math.round(sum / this.rewriteTimes.length);
    }
    
    // Calculate cache hit rate
    const cacheTotal = this.cacheHits + this.cacheMisses;
    if (cacheTotal > 0) {
      this.metrics.cacheHitRate = this.cacheHits / cacheTotal;
    }
    
    // Calculate error rate
    if (this.total > 0) {
      this.metrics.errorRate = this.errors / this.total;
    }
    
    // Estimate memory usage (simplified)
    if (typeof process !== 'undefined' && (process as any).memoryUsage) {
      const usage = (process as any).memoryUsage();
      this.metrics.memoryUsage = Math.round(usage.heapUsed / 1024 / 1024); // MB
    }
    
    this.metrics.lastUpdated = Date.now();
  }
}

export default PerformanceOptimizer;

import SceneRewriter from './SceneRewriter';
import type { Scene, ContinuityIssue, ReaderKnowledge } from '../../shared/types';
import type { RewriteResult } from './SceneRewriter';
import AIServiceManager from '../ai/AIServiceManager';

export interface BatchRewriteProgress {
  totalScenes: number;
  completedScenes: number;
  currentSceneId?: string;
  currentSceneTitle?: string;
  phase: 'preparing' | 'analyzing' | 'rewriting' | 'complete' | 'error';
  message: string;
  results: Map<string, RewriteResult>;
  errors: Map<string, string>;
}

export interface BatchRewriteOptions {
  sceneIds?: string[];  // Specific scenes, or all moved if not provided
  skipIfNoIssues?: boolean;
  preserveElements?: Map<string, string[]>; // Per-scene preservation
  progressCallback?: (progress: BatchRewriteProgress) => void;
}

class RewriteOrchestrator {
  private sceneRewriter: SceneRewriter;
  private aiManager: AIServiceManager;
  private currentBatch?: AbortController;

  constructor(aiManager?: AIServiceManager) {
    this.aiManager = aiManager || new AIServiceManager();
    this.sceneRewriter = new SceneRewriter(this.aiManager);
  }

  /**
   * Rewrite multiple moved scenes in dependency order
   */
  async rewriteMovedScenes(
    manuscript: { scenes: Scene[]; currentOrder: string[] },
    options: BatchRewriteOptions = {}
  ): Promise<BatchRewriteProgress> {
    // Cancel any existing batch
    this.cancelBatch();
    this.currentBatch = new AbortController();

    const progress: BatchRewriteProgress = {
      totalScenes: 0,
      completedScenes: 0,
      phase: 'preparing',
      message: 'Preparing batch rewrite...',
      results: new Map(),
      errors: new Map()
    };

    try {
      // Step 1: Identify scenes to rewrite
      const scenesToRewrite = this.identifyScenesToRewrite(manuscript, options);
      progress.totalScenes = scenesToRewrite.length;

      if (scenesToRewrite.length === 0) {
        progress.phase = 'complete';
        progress.message = 'No scenes need rewriting';
        return progress;
      }

      // Step 2: Order scenes by dependency (narrative) order
      const orderedScenes = this.orderByDependency(scenesToRewrite, manuscript.currentOrder);

      progress.phase = 'analyzing';
      progress.message = `Analyzing ${scenesToRewrite.length} scenes...`;
      options.progressCallback?.(progress);

      // Step 3: Process each scene
      for (const sceneId of orderedScenes) {
        if (this.currentBatch?.signal.aborted) {
          progress.phase = 'error';
          progress.message = 'Batch rewrite cancelled';
          break;
        }

        const scene = manuscript.scenes.find(s => s.id === sceneId);
        if (!scene) {
          progress.errors.set(sceneId, 'Scene not found');
          progress.completedScenes++;
          options.progressCallback?.(progress);
          continue;
        }

        progress.currentSceneId = sceneId;
        progress.currentSceneTitle = this.getSceneTitle(scene);
        progress.phase = 'rewriting';
        progress.message = `Rewriting scene ${progress.completedScenes + 1} of ${progress.totalScenes}`;
        options.progressCallback?.(progress);

        try {
          // Get issues from analysis
          const issues = scene.continuityAnalysis?.issues || [];
          if (options.skipIfNoIssues && issues.length === 0) {
            progress.completedScenes++;
            continue;
          }

          // Build reader context from previous scenes
          const readerContext = this.buildReaderContext(
            manuscript,
            sceneId,
            progress.results
          );

          // Get previous scenes (including any already rewritten)
          const previousScenes = this.getPreviousScenes(
            manuscript,
            sceneId,
            progress.results
          );

          // Rewrite the scene
          const result = await this.sceneRewriter.rewriteScene({
            scene,
            issuesFound: issues,
            readerContext,
            previousScenes,
            preserveElements: options.preserveElements?.get(sceneId) || []
          });

          if (result.success) {
            progress.results.set(sceneId, result);
          } else {
            progress.errors.set(sceneId, result.error || 'Unknown error');
          }
        } catch (error) {
           
          console.error(`[RewriteOrchestrator] Failed to rewrite scene ${sceneId}:`, error);
          progress.errors.set(sceneId, error instanceof Error ? error.message : 'Unknown error');
        }

        progress.completedScenes++;
        options.progressCallback?.(progress);

        // Add small delay between scenes to prevent rate limiting
        if (progress.completedScenes < progress.totalScenes) {
          await this.delay(500);
        }
      }

      progress.phase = progress.errors.size > 0 ? 'error' : 'complete';
      progress.message = this.generateCompletionMessage(progress);

    } catch (error) {
       
      console.error('[RewriteOrchestrator] Batch rewrite error:', error);
      progress.phase = 'error';
      progress.message = 'Batch rewrite failed';
    }

    this.currentBatch = undefined;
    return progress;
  }

  /**
   * Cancel the current batch operation
   */
  cancelBatch(): void {
    if (this.currentBatch) {
      this.currentBatch.abort();
    }
  }

  private identifyScenesToRewrite(
    manuscript: { scenes: Scene[] },
    options: BatchRewriteOptions
  ): string[] {
    if (options.sceneIds) {
      return options.sceneIds;
    }

    // Default: all moved scenes with issues
    return manuscript.scenes
      .filter(scene =>
        scene.hasBeenMoved &&
        !!scene.continuityAnalysis?.issues &&
        scene.continuityAnalysis.issues.length > 0
      )
      .map(scene => scene.id);
  }

  private orderByDependency(
    sceneIds: string[],
    currentOrder: string[]
  ): string[] {
    // Process scenes in their current narrative order
    // This ensures reader context builds correctly
    return sceneIds.sort((a, b) => {
      const aIndex = currentOrder.indexOf(a);
      const bIndex = currentOrder.indexOf(b);
      return aIndex - bIndex;
    });
  }

  private buildReaderContext(
    manuscript: { scenes: Scene[]; currentOrder: string[] },
    sceneId: string,
    rewrittenScenes: Map<string, RewriteResult>
  ): ReaderKnowledge {
    const context: ReaderKnowledge = {
      knownCharacters: new Set<string>(),
      establishedTimeline: [],
      revealedPlotPoints: [],
      establishedSettings: []
    };

    const sceneIndex = manuscript.currentOrder.indexOf(sceneId);
    if (sceneIndex <= 0) return context;

    // Include all scenes before this one
    for (let i = 0; i < sceneIndex; i++) {
      const prevSceneId = manuscript.currentOrder[i];
      const prevScene = manuscript.scenes.find(s => s.id === prevSceneId);
      if (!prevScene) continue;

      // Use rewritten version if available
      const rewritten = rewrittenScenes.get(prevSceneId);
      if (rewritten && rewritten.rewrittenText) {
        // For now, use original metadata; could extract from rewritten text later
        prevScene.characters?.forEach(char => context.knownCharacters.add(char));
      } else {
        prevScene.characters?.forEach(char => context.knownCharacters.add(char));
        prevScene.timeMarkers?.forEach(marker => {
          context.establishedTimeline.push({ label: marker });
        });
        prevScene.locationMarkers?.forEach(loc => {
          context.establishedSettings.push({ name: loc });
        });
      }
    }

    return context;
  }

  private getPreviousScenes(
    manuscript: { scenes: Scene[]; currentOrder: string[] },
    sceneId: string,
    rewrittenScenes: Map<string, RewriteResult>
  ): Scene[] {
    const sceneIndex = manuscript.currentOrder.indexOf(sceneId);
    if (sceneIndex <= 0) return [];

    // Get last 3 scenes before this one
    const startIndex = Math.max(0, sceneIndex - 3);
    const previousScenes: Scene[] = [];

    for (let i = startIndex; i < sceneIndex; i++) {
      const prevSceneId = manuscript.currentOrder[i];
      const prevScene = manuscript.scenes.find(s => s.id === prevSceneId);
      if (!prevScene) continue;

      // Use rewritten version if available
      const rewritten = rewrittenScenes.get(prevSceneId);
      if (rewritten && rewritten.rewrittenText) {
        previousScenes.push({
          ...prevScene,
          text: rewritten.rewrittenText,
          currentRewrite: rewritten.rewrittenText
        });
      } else {
        previousScenes.push(prevScene);
      }
    }

    return previousScenes;
  }

  private getSceneTitle(scene: Scene): string {
    // Try to extract a title from the scene
    const firstLine = (scene.text || '').split('\n')[0];
    if (firstLine && firstLine.length < 100) {
      return firstLine;
    }
    return `Scene ${scene.position + 1}`;
  }

  private generateCompletionMessage(progress: BatchRewriteProgress): string {
    const successful = progress.completedScenes - progress.errors.size;

    if (progress.errors.size === 0) {
      return `Successfully rewrote ${successful} scene${successful !== 1 ? 's' : ''}`;
    } else if (successful === 0) {
      return `Failed to rewrite ${progress.errors.size} scene${progress.errors.size !== 1 ? 's' : ''}`;
    } else {
      return `Rewrote ${successful} scene${successful !== 1 ? 's' : ''}, ${progress.errors.size} failed`;
    }
  }

  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }
}

export default RewriteOrchestrator;
